   274→            opacity: 0.9;
   275→        }
   276→
   277→        .stat-card .value {
   278→            font-size: 2em;
   279→            font-weight: 700;
   280→        }
   281→
   282→        .matrix-section {
   283→            margin-top: 30px;
   284→        }
   285→
   286→        .matrix-controls {
   287→            display: flex;
   288→            gap: 20px;
   289→            margin-bottom: 20px;
   290→            align-items: center;
   291→        }
   292→
   293→        .filter-section {
   294→            flex: 1;
   295→            background: #f8f9ff;
   296→            padding: 15px;
   297→            border-radius: 8px;
   298→            border: 1px solid #e2e8f0;
   299→        }
   300→
   301→        .filter-section h4 {
   302→            margin-bottom: 10px;
   303→            color: #374151;
   304→        }
   305→
   306→        .filter-controls {
   307→            display: flex;
   308→            gap: 10px;
   309→            margin-bottom: 15px;
   310→        }
   311→
   312→        .filter-btn {
