   356→
   357→        .toggle-section {
   358→            background: #f8f9ff;
   359→            padding: 15px;
   360→            border-radius: 8px;
   361→            border: 1px solid #e2e8f0;
   362→            text-align: center;
   363→        }
   364→
   365→        .toggle-section h4 {
   366→            margin-bottom: 10px;
   367→            color: #374151;
   368→        }
   369→
   370→        .toggle-buttons {
   371→            display: flex;
   372→            border-radius: 6px;
   373→            overflow: hidden;
   374→            border: 1px solid #d1d5db;
   375→        }
   376→
   377→        .toggle-btn {
   378→            flex: 1;
   379→            padding: 8px 16px;
   380→            background: white;
   381→            border: none;
   382→            cursor: pointer;
   383→            font-size: 0.9em;
   384→            transition: all 0.3s;
