   391→
   392→        .matrix-table {
   393→            width: 100%;
   394→            border-collapse: collapse;
   395→            background: white;
   396→            border-radius: 8px;
   397→            overflow: hidden;
   398→            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.08);
   399→        }
   400→
   401→        .matrix-table th {
