   520→            border: 2px solid #ff6b35;
   521→            border-radius: 12px;
   522→            padding: 20px;
   523→        }
   524→
   525→        .unclassified-fund-item {
   526→            display: grid;
   527→            grid-template-columns: 2fr 1fr 1fr 1fr 80px;
   528→            gap: 15px;
   529→            padding: 15px;
   530→            background: white;
   531→            border-radius: 8px;
   532→            margin-bottom: 10px;
   533→            border: 1px solid #e2e8f0;
