   535→        }
   536→
   537→        .unclassified-fund-item:hover {
   538→            background: #f8f9ff;
   539→            border-color: #667eea;
   540→        }
   541→
   542→        .unclassified-fund-item .fund-name {
   543→            font-weight: 600;
   544→            color: #333;
   545→            font-size: 0.9em;
   546→        }
   547→
   548→        .unclassified-fund-item .asset-type {
   549→            background: #ff6b35;
   550→            color: white;
   551→            padding: 4px 8px;
   552→            border-radius: 4px;
   553→            font-size: 0.8em;
   554→            text-align: center;
   555→        }
   556→
   557→        .unclassified-fund-item select,
   558→        .unclassified-fund-item input {
   559→            padding: 8px;
   560→            border: 1px solid #d1d5db;
   561→            border-radius: 6px;
   562→            font-size: 0.9em;
   563→        }
   564→
   565→        .unclassified-fund-item select:focus,
   566→        .unclassified-fund-item input:focus {
   567→            outline: none;
   568→            border-color: #667eea;
   569→            box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
   570→        }
   571→
   572→        .unclassified-fund-header {
   573→            display: grid;
   574→            grid-template-columns: 2fr 1fr 1fr 1fr 80px;
   575→            gap: 15px;
   576→            padding: 10px 15px;
   577→            background: #f8f9ff;
   578→            border-radius: 8px;
   579→            margin-bottom: 15px;
   580→            font-weight: 600;
   581→            color: #667eea;
   582→            font-size: 0.9em;
   583→        }
   584→
   585→        .batch-add-individual {
   586→            background: #10b981;
   587→            color: white;
   588→            border: none;
   589→            border-radius: 4px;
   590→            padding: 6px 12px;
   591→            font-size: 0.8em;
   592→            cursor: pointer;
   593→            transition: background 0.3s;
