   801→
   802→                        // 优先使用外部代码，如果没有则使用资产代码
   803→                        const matchKey = externalCode || assetCode;
   804→
   805→                        if (matchKey && style && equityPosition >= 0) {
   806→                            fundClassifications[matchKey] = {
   807→                                assetClass: assetClass,
   808→                                style: style,
   809→                                equityPosition: equityPosition
   810→                            };
   811→                            successCount++;
   812→                        } else {
   813→                            skipCount++;
   814→                        }
   815→                    });
   816→
   817→                    isClassificationLoaded = true;
   818→                    loading.classList.remove('show');
   819→                    classificationResult.innerHTML = `<div class="success">✅ 成功加载 ${successCount} 个基金分类${skipCount > 0 ? ` (跳过 ${skipCount} 个无效记录)` : ''}</div>`;
   820→
   821→                    console.log('基金分类数据加载完成，数量:', successCount);
   822→
   823→                    // 如果已经上传了持仓表，重新处理
   824→                    if (holdingData.length > 0) {
   825→                        processData();
   826→                    }
   827→                } catch (error) {
   828→                    loading.classList.remove('show');
   829→                    classificationResult.innerHTML = `<div class="error">❌ 基金分类表处理失败: ${error.message}</div>`;
   830→                    console.error('基金分类表处理失败:', error);
   831→                }
   832→            };
   833→            reader.readAsArrayBuffer(file);
   834→        }
