   877→        // 处理数据
   878→        function processData() {
   879→            if (holdingData.length === 0) return;
   880→
   881→            const productResults = {};
   882→            const productNetAssets = {}; // 存储各产品的净资产
   883→            let matchedCount = 0;
   884→            let unmatchedCount = 0;
   885→
   886→            // 存储未分类的含权基金
   887→            const unclassifiedEquityFunds = new Map(); // 使用Map避免重复，key为matchKey
   888→
   889→            // 调试：检查Excel文件的列名
   890→            if (holdingData.length > 0) {
   891→                console.log('持仓表列名:', Object.keys(holdingData[0]));
   892→            }
   893→
   894→            // 首先计算各产品的净资产
   895→            holdingData.forEach(row => {
   896→                const productName = normalizeText(row['产品简称']);
   897→                const assetName = normalizeText(row['资产简称']);
   898→                const holdingAmount = parseFloat(row['日终市值(元)-产品法估值']) || 0;
   899→
   900→                if (!productName || holdingAmount === 0) return;
   901→
   902→                if (!productNetAssets[productName]) {
   903→                    productNetAssets[productName] = 0;
   904→                }
   905→
   906→                // 检查是否为正回购
   907→                const isRepo = assetName && assetName.includes('正回购');
   908→
   909→                if (isRepo) {
   910→                    // 正回购从净资产中减去
   911→                    productNetAssets[productName] -= holdingAmount;
   912→                } else {
   913→                    // 其他资产加入净资产
   914→                    productNetAssets[productName] += holdingAmount;
   915→                }
   916→            });
   917→
   918→            // 处理每条持仓记录
   919→            holdingData.forEach(row => {
   920→                const productName = normalizeText(row['产品简称']);
   921→                const assetName = normalizeText(row['资产简称']);
   922→                const holdingAmount = parseFloat(row['日终市值(元)-产品法估值']) || 0;
   923→                const assetSubClass = normalizeText(row['新版资产小类']);
   924→
   925→                if (!productName || !assetName || holdingAmount === 0) return;
   926→
   927→                // 使用外部代码或资产代码匹配
   928→                const matchKey = getFundMatchKey(row);
   929→                const classification = fundClassifications[matchKey];
   930→
   931→                // 检测含权资产是否已分类
   932→                if (!classification && matchKey && EQUITY_ASSET_CLASSES.includes(assetSubClass)) {
   933→                    // 这是一个需要分类的含权基金，但未在分类表中找到
   934→                    if (!unclassifiedEquityFunds.has(matchKey)) {
   935→                        unclassifiedEquityFunds.set(matchKey, {
   936→                            matchKey: matchKey,
   937→                            assetName: assetName,
   938→                            assetSubClass: assetSubClass,
   939→                            externalCode: normalizeText(row['外部代码']),
   940→                            assetCode: normalizeText(row['资产代码'])
   941→                        });
   942→                    }
   943→                }
   944→
   945→                // 如果没有分类信息，跳过处理
   946→                if (!classification) {
   947→                    if (matchKey) {
   948→                        unmatchedCount++;
   949→                        console.log('未匹配到基金:', assetName, '匹配键:', matchKey);
   950→                    }
   951→                    return;
   952→                }
   953→
   954→                matchedCount++;
   955→                const equityExposure = holdingAmount * (classification.equityPosition / 100);
   956→
   957→                if (!productResults[productName]) {
   958→                    productResults[productName] = {};
   959→                }
   960→
   961→                if (!productResults[productName][classification.style]) {
   962→                    productResults[productName][classification.style] = 0;
   963→                }
   964→
   965→                productResults[productName][classification.style] += equityExposure;
   966→            });
   967→
   968→            console.log(`匹配统计: 成功匹配 ${matchedCount} 条, 未匹配 ${unmatchedCount} 条`);
   969→
   970→            // 计算占比
   971→            Object.keys(productResults).forEach(productName => {
   972→                const totalEquity = Object.values(productResults[productName]).reduce((sum, value) => sum + value, 0);
   973→                Object.keys(productResults[productName]).forEach(style => {
   974→                    const amount = productResults[productName][style];
   975→                    productResults[productName][style] = {
   976→                        amount: amount,
   977→                        percentage: totalEquity > 0 ? (amount / totalEquity) * 100 : 0
   978→                    };
   979→                });
   980→            });
   981→
   982→            analysisResults = productResults;
   983→
   984→            // 初始化选中所有产品
   985→            selectedProducts.clear();
   986→            Object.keys(productResults).forEach(product => {
   987→                selectedProducts.add(product);
   988→            });
   989→
   990→            renderResults();
   991→            renderDetailTable(calculateDetailData());
   992→
   993→            // 渲染未分类含权基金提示
   994→            renderUnclassifiedEquityFunds(unclassifiedEquityFunds);
   995→
   996→            // 显示匹配统计
   997→            const uploadResult = document.getElementById('uploadResult');
   998→            uploadResult.innerHTML = `<div class="success">✅ 数据处理完成: 成功匹配 ${matchedCount} 条持仓${unmatchedCount > 0 ? `, 未匹配 ${unmatchedCount} 条` : ''}</div>`;
   999→        }
