  1238→                '#43e97b', '#fa709a', '#fee140', '#ff9a9e', '#a8edea'
  1239→            ];
  1240→
  1241→            new Chart(ctx, {
  1242→                type: 'doughnut',
  1243→                data: {
  1244→                    labels: labels,
  1245→                    datasets: [{
  1246→                        data: values,
  1247→                        backgroundColor: colors.slice(0, labels.length),
  1248→                        borderWidth: 2,
  1249→                        borderColor: '#fff'
  1250→                    }]
  1251→                },
  1252→                options: {
