  1268→        // 计算详细数据（包括产品净资产）
  1269→        function calculateDetailData() {
  1270→            const detailData = [];
  1271→            const productNetAssets = {}; // 存储各产品的净资产
  1272→
  1273→            // 首先计算各产品的净资产
  1274→            holdingData.forEach(row => {
  1275→                const productName = row['产品简称'];
  1276→                const assetName = row['资产简称'];
  1277→                const holdingAmount = parseFloat(row['日终市值(元)-产品法估值']) || 0;
  1278→
  1279→                if (!productName || holdingAmount === 0) return;
  1280→
  1281→                if (!productNetAssets[productName]) {
  1282→                    productNetAssets[productName] = 0;
  1283→                }
  1284→
  1285→                // 检查是否为正回购
  1286→                const isRepo = assetName && assetName.includes('正回购');
  1287→
  1288→                if (isRepo) {
  1289→                    // 正回购从净资产中减去
  1290→                    productNetAssets[productName] -= holdingAmount;
  1291→                } else {
  1292→                    // 其他资产加入净资产
  1293→                    productNetAssets[productName] += holdingAmount;
  1294→                }
  1295→            });
  1296→
  1297→            // 然后生成详细数据
  1298→            holdingData.forEach(row => {
  1299→                const productName = row['产品简称'];
  1300→                const assetName = row['资产简称'];
  1301→                const holdingAmount = parseFloat(row['日终市值(元)-产品法估值']) || 0;
  1302→
  1303→                if (!productName || !assetName || holdingAmount === 0) return;
  1304→
  1305→                // 使用新的匹配键逻辑
  1306→                const matchKey = getFundMatchKey(row);
  1307→                const classification = fundClassifications[matchKey];
  1308→                if (!classification) return;
  1309→
  1310→                const equityExposure = holdingAmount * (classification.equityPosition / 100);
  1311→
  1312→                detailData.push({
  1313→                    productName,
  1314→                    assetName,
  1315→                    assetClass: classification.assetClass,
  1316→                    style: classification.style,
  1317→                    equityPosition: classification.equityPosition,
  1318→                    holdingAmount,
  1319→                    equityExposure,
  1320→                    productNetAsset: productNetAssets[productName]
  1321→                });
  1322→            });
  1323→
  1324→            return detailData;
  1325→        }
