  1338→                row.equityExposureRatio = row.productNetAsset > 0 ? (row.equityExposure / row.productNetAsset * 100) : 0;
  1339→            });
  1340→
  1341→            // 按照产品名称、风格、权益暴露占比排序
  1342→            filteredData.sort((a, b) => {
  1343→                // 首先按产品名称排序
  1344→                if (a.productName < b.productName) return -1;
  1345→                if (a.productName > b.productName) return 1;
  1346→
  1347→                // 如果产品名称相同，按风格排序
  1348→                if (a.style < b.style) return -1;
  1349→                if (a.style > b.style) return 1;
  1350→
  1351→                // 如果产品名称和风格都相同，按权益暴露占比降序排序
  1352→                return b.equityExposureRatio - a.equityExposureRatio;
  1353→            });
  1354→
  1355→            filteredData.forEach(row => {
