  1462→        // 导出分析结果到Excel
  1463→        function exportResultsToExcel() {
  1464→            if (Object.keys(analysisResults).length === 0) {
  1465→                alert('没有可导出的数据，请先上传并处理数据');
  1466→                return;
  1467→            }
  1468→
  1469→            try {
