  1477→
  1478→                // Sheet 2: 产品风格汇总（占比）
  1479→                const summaryPercentData = createMatrixData('percentage');
  1480→                const ws2 = XLSX.utils.aoa_to_sheet(summaryPercentData);
  1481→                XLSX.utils.book_append_sheet(wb, ws2, '产品风格汇总-占比');
  1482→
  1483→                // Sheet 3: 持仓明细
  1484→                const detailData = createDetailData();
