  1499→        // 创建矩阵数据（用于导出）
  1500→        function createMatrixData(type) {
  1501→            const data = [];
  1502→
  1503→            // 获取所有风格并排序
  1504→            const styleOrder = ['红利', '均衡', '科技成长', '周期', '价值'];
  1505→            const allStyles = new Set();
  1506→            Object.values(analysisResults).forEach(product => {
  1507→                Object.keys(product).forEach(style => allStyles.add(style));
  1508→            });
  1509→            const styles = Array.from(allStyles).sort((a, b) => {
  1510→                const indexA = styleOrder.indexOf(a);
  1511→                const indexB = styleOrder.indexOf(b);
  1512→                if (indexA === -1 && indexB === -1) return a.localeCompare(b);
  1513→                if (indexA === -1) return 1;
  1514→                if (indexB === -1) return -1;
  1515→                return indexA - indexB;
  1516→            });
  1517→
  1518→            // 表头
  1519→            const header = ['产品名称', ...styles];
  1520→            data.push(header);
  1521→
  1522→            // 数据行 - 只导出选中的产品
  1523→            Array.from(selectedProducts).forEach(productName => {
  1524→                const product = analysisResults[productName];
  1525→                const row = [productName];
  1526→
  1527→                styles.forEach(style => {
  1528→                    const styleData = product[style];
  1529→                    if (styleData) {
  1530→                        if (type === 'amount') {
  1531→                            // 金额（万元）
  1532→                            row.push(Math.round(styleData.amount / 10000));
  1533→                        } else {
  1534→                            // 占比（整数%）
  1535→                            row.push(Math.round(styleData.percentage) + '%');
  1536→                        }
  1537→                    } else {
  1538→                        row.push('-');
  1539→                    }
  1540→                });
  1541→
  1542→                data.push(row);
  1543→            });
  1544→
  1545→            return data;
  1546→        }
  1547→
  1548→        // 创建详细数据（用于导出）
  1549→        function createDetailData() {
  1550→            const data = [];
  1551→
  1552→            // 表头
  1553→            data.push(['产品名称', '基金名称', '资产小类', '风格', '权益仓位', '持仓金额(万)', '权益暴露金额(万)', '权益暴露占比']);
  1554→
  1555→            // 获取详细数据
  1556→            const detailData = calculateDetailData();
  1557→
  1558→            // 筛选选中的产品
  1559→            const filteredData = detailData.filter(row => selectedProducts.has(row.productName));
  1560→
  1561→            // 计算权益暴露占比
  1562→            filteredData.forEach(row => {
  1563→                row.equityExposureRatio = row.productNetAsset > 0
  1564→                    ? (row.equityExposure / row.productNetAsset * 100)
  1565→                    : 0;
  1566→            });
  1567→
  1568→            // 排序
  1569→            filteredData.sort((a, b) => {
  1570→                if (a.productName < b.productName) return -1;
  1571→                if (a.productName > b.productName) return 1;
  1572→                if (a.style < b.style) return -1;
  1573→                if (a.style > b.style) return 1;
  1574→                return b.equityExposureRatio - a.equityExposureRatio;
  1575→            });
  1576→
  1577→            // 数据行
  1578→            filteredData.forEach(row => {
  1579→                data.push([
  1580→                    row.productName,
  1581→                    row.assetName,
  1582→                    row.assetClass,
  1583→                    row.style,
  1584→                    row.equityPosition + '%',
  1585→                    Math.round(row.holdingAmount / 10000),
  1586→                    Math.round(row.equityExposure / 10000),
  1587→                    row.equityExposureRatio.toFixed(2) + '%'
  1588→                ]);
  1589→            });
  1590→
  1591→            return data;
  1592→        }
