   130→    <script>
   131→        // Configuration
   132→        const MAPPING = [
   133→            { key: '悦动稳享短持', name: '稳享' },
   134→            { key: '丰利/增盈', name: '合享' },
   135→            { key: '逸动短持', name: '逸动' },
   136→            { key: '悦动短持/定开', name: '悦动' },
   137→            { key: '灵动短持', name: '灵动' },
   138→            { key: '兴动短持', name: '兴动' }
   139→        ];
   140→
   141→        // Expected Columns
   142→        const COLS = {
   143→            CATEGORY: '母分类',
   144→            NET_ASSETS: '净资产(亿)',
   145→            DURATION: '组合久期',
   146→            LEVERAGE: '杠杆',
   147→            EQUITY: '权益仓位',
   148→            DERIVATIVES: '衍生品仓位',
   149→            FLAG: '母子标志'
   150→        };
   151→
   152→        let generatedData = [];
   153→
   154→        async function readExcel(file) {
   155→            return new Promise((resolve, reject) => {
   156→                const reader = new FileReader();
   157→                reader.onload = (e) => {
   158→                    try {
   159→                        const data = new Uint8Array(e.target.result);
   160→                        const workbook = XLSX.read(data, { type: 'array' });
   161→                        const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
   162→                        const jsonData = XLSX.utils.sheet_to_json(firstSheet);
   163→
   164→                        // Normalize keys (trim spaces)
   165→                        const normalizedData = jsonData.map(row => {
   166→                            const newRow = {};
   167→                            Object.keys(row).forEach(key => {
   168→                                newRow[key.trim()] = row[key];
   169→                            });
   170→                            return newRow;
   171→                        });
   172→
   173→                        resolve(normalizedData);
   174→                    } catch (err) {
   175→                        reject(err);
   176→                    }
   177→                };
   178→                reader.onerror = reject;
   179→                reader.readAsArrayBuffer(file);
   180→            });
   181→        }
   182→
   183→        function extractDateFromFilename(filename) {
   184→            // Match YYYY-MM-DD or YYYYMMDD
   185→            const match = filename.match(/(\d{4}[-年]?\d{2}[-月]?\d{2})/);
   186→            return match ? match[0] : '当期';
   187→        }
   188→
   189→        async function processFiles() {
   190→            const currentFile = document.getElementById('currentFile').files[0];
   191→            const compareFile = document.getElementById('compareFile').files[0];
   192→
   193→            if (!currentFile || !compareFile) {
   194→                alert("请上传两个文件！");
   195→                return;
   196→            }
   197→
   198→            try {
   199→                let currentData = await readExcel(currentFile);
   200→                let compareData = await readExcel(compareFile);
   201→
   202→                const filterData = (data) => {
   203→                    return data.filter(row => {
   204→                        const flag = row[COLS.FLAG];
   205→                        if (!flag) return false;
   206→                        const val = String(flag).trim();
   207→                        return val === '母产品' || val === '非母子产品';
   208→                    });
   209→                };
   210→
   211→                currentData = filterData(currentData);
   212→                compareData = filterData(compareData);
   213→
   214→                const currentDate = extractDateFromFilename(currentFile.name);
   215→                const compareDate = extractDateFromFilename(compareFile.name);
   216→
   217→                generateTable(currentData, compareData, currentDate, compareDate);
   218→                document.getElementById('exportBtn').classList.remove('hidden');
   219→
   220→                // Store dates for export
   221→                window.currentDateStr = currentDate;
   222→                window.compareDateStr = compareDate;
   223→            } catch (error) {
   224→                console.error(error);
   225→                alert("处理文件时出错: " + error.message);
   226→            }
   227→        }
   228→
   229→        const parseNum = (val) => {
   230→            if (typeof val === 'number') return val;
   231→            if (typeof val === 'string') {
   232→                val = val.trim();
   233→                if (val.endsWith('%')) {
   234→                    return parseFloat(val) / 100;
   235→                }
   236→                return parseFloat(val);
   237→            }
   238→            return 0;
   239→        };
   240→
   241→        function calculateTotalScale(data, categoryKey) {
   242→            const items = data.filter(row => row[COLS.CATEGORY] === categoryKey);
   243→            let totalAssets = 0;
   244→            items.forEach(row => {
   245→                let assets = row[COLS.NET_ASSETS];
   246→                assets = parseNum(assets) || 0;
   247→                totalAssets += assets;
   248→            });
   249→            return totalAssets;
   250→        }
   251→
   252→        function calculateWeightedAvg(data, categoryKey, metricKey) {
   253→            const items = data.filter(row => row[COLS.CATEGORY] === categoryKey);
   254→            if (items.length === 0) return null;
   255→
   256→            let totalWeighted = 0;
   257→            let totalAssets = 0;
   258→
   259→            items.forEach(row => {
   260→                let assets = row[COLS.NET_ASSETS];
   261→                let metric = row[metricKey];
   262→
   263→                assets = parseNum(assets) || 0;
   264→                metric = parseNum(metric) || 0;
   265→
   266→                totalWeighted += metric * assets;
   267→                totalAssets += assets;
   268→            });
   269→
   270→            return totalAssets === 0 ? 0 : totalWeighted / totalAssets;
   271→        }
   272→
   273→        function formatValue(value, type) {
   274→            if (value === null || value === undefined || isNaN(value)) return '-';
   275→            if (Math.abs(value) < 0.000001) return '-';
   276→
   277→            if (type === 'percent') {
   278→                return (value * 100).toFixed(2) + '%';
   279→            } else if (type === 'percent_int') {
   280→                return (value * 100).toFixed(0) + '%';
   281→            } else if (type === 'decimal') {
   282→                return value.toFixed(2);
   283→            }
   284→            return value;
   285→        }
   286→
   287→        function generateTable(currentData, compareData, currentDate, compareDate) {
   288→            const diffLabel = `（相比于${compareDate}）`;
   289→
   290→            let html = `
   291→                <table class="custom-table" id="resultTable">
   292→                    <thead>
   293→                        <tr>
   294→                            <th class="header-blue">产品系列</th>
   295→                            <th class="header-blue">规模(亿)</th>
   296→                            <th class="header-blue">${currentDate}久期</th>
   297→                            <th class="header-blue">久期变化<br><span class="text-xs font-normal">${diffLabel}</span></th>
   298→                            <th class="header-green">${currentDate}杠杆</th>
   299→                            <th class="header-green">杠杆变化<br><span class="text-xs font-normal">${diffLabel}</span></th>
   300→                            <th class="header-red">${currentDate}权益仓位</th>
   301→                            <th class="header-red">权益仓位变化<br><span class="text-xs font-normal">${diffLabel}</span></th>
   302→                            <th class="header-yellow">${currentDate}衍生品仓位</th>
   303→                            <th class="header-yellow">衍生品仓位变化<br><span class="text-xs font-normal">${diffLabel}</span></th>
   304→                        </tr>
   305→                    </thead>
   306→                    <tbody>
   307→            `;
   308→
   309→            generatedData = []; // Reset for export
   310→
   311→            MAPPING.forEach(item => {
   312→                const rowData = { series: item.name };
   313→
   314→                // Scale
   315→                rowData.scale = calculateTotalScale(currentData, item.key);
   316→
   317→                // Duration
   318→                const curDur = calculateWeightedAvg(currentData, item.key, COLS.DURATION);
   319→                const oldDur = calculateWeightedAvg(compareData, item.key, COLS.DURATION);
   320→                rowData.dur = curDur;
   321→                rowData.durChg = (curDur !== null && oldDur !== null) ? curDur - oldDur : null;
   322→
   323→                // Leverage
   324→                const curLev = calculateWeightedAvg(currentData, item.key, COLS.LEVERAGE);
   325→                const oldLev = calculateWeightedAvg(compareData, item.key, COLS.LEVERAGE);
   326→                rowData.lev = curLev;
   327→                rowData.levChg = (curLev !== null && oldLev !== null) ? curLev - oldLev : null;
   328→
   329→                // Equity
   330→                const curEq = calculateWeightedAvg(currentData, item.key, COLS.EQUITY);
   331→                const oldEq = calculateWeightedAvg(compareData, item.key, COLS.EQUITY);
   332→                rowData.eq = curEq;
   333→                rowData.eqChg = (curEq !== null && oldEq !== null) ? curEq - oldEq : null;
   334→
   335→                // Derivatives
   336→                const curDer = calculateWeightedAvg(currentData, item.key, COLS.DERIVATIVES);
   337→                const oldDer = calculateWeightedAvg(compareData, item.key, COLS.DERIVATIVES);
   338→                rowData.der = curDer;
   339→                rowData.derChg = (curDer !== null && oldDer !== null) ? curDer - oldDer : null;
   340→
   341→                generatedData.push(rowData);
   342→
   343→                html += `
   344→                    <tr>
   345→                        <td class="series-name">${item.name}</td>
   346→                        <td>${formatValue(rowData.scale, 'decimal')}</td>
   347→                        <td>${formatValue(rowData.dur, 'decimal')}</td>
   348→                        <td class="change-text">${formatValue(rowData.durChg, 'decimal')}</td>
   349→                        <td>${formatValue(rowData.lev, 'percent_int')}</td>
   350→                        <td class="change-text">${formatValue(rowData.levChg, 'percent_int')}</td>
   351→                        <td>${formatValue(rowData.eq, 'percent')}</td>
   352→                        <td class="change-text">${formatValue(rowData.eqChg, 'percent')}</td>
   353→                        <td>${formatValue(rowData.der, 'percent')}</td>
   354→                        <td class="change-text">${formatValue(rowData.derChg, 'percent')}</td>
   355→                    </tr>
   356→                `;
   357→            });
   358→
   359→            html += `</tbody></table>`;
   360→            document.getElementById('resultContainer').innerHTML = html;
   361→        }
   362→
   363→        function exportToExcel() {
   364→            const currentDate = window.currentDateStr || '当期';
   365→            const compareDate = window.compareDateStr || '对比期';
   366→            const diffLabel = `（相比于${compareDate}）`;
   367→
   368→            // Data prep
   369→            const wsData = [
   370→                [
   371→                    "产品系列",
   372→                    "规模(亿)",
   373→                    `${currentDate}久期`, `久期变化${diffLabel}`,
   374→                    `${currentDate}杠杆`, `杠杆变化${diffLabel}`,
   375→                    `${currentDate}权益仓位`, `权益仓位变化${diffLabel}`,
   376→                    `${currentDate}衍生品仓位`, `衍生品仓位变化${diffLabel}`
   377→                ]
   378→            ];
   379→
   380→            generatedData.forEach(row => {
   381→                wsData.push([
   382→                    row.series,
   383→                    formatValue(row.scale, 'decimal'),
   384→                    formatValue(row.dur, 'decimal'),
   385→                    formatValue(row.durChg, 'decimal'),
   386→                    formatValue(row.lev, 'percent_int'),
   387→                    formatValue(row.levChg, 'percent_int'),
   388→                    formatValue(row.eq, 'percent'),
   389→                    formatValue(row.eqChg, 'percent'),
   390→                    formatValue(row.der, 'percent'),
   391→                    formatValue(row.derChg, 'percent')
   392→                ]);
   393→            });
   394→
   395→            // Create Sheet
   396→            const ws = XLSX.utils.aoa_to_sheet(wsData);
   397→
   398→            // Define Styles
   399→            const borderStyle = { top: { style: "thin" }, bottom: { style: "thin" }, left: { style: "thin" }, right: { style: "thin" } };
   400→            const alignCenter = { horizontal: "center", vertical: "center", wrapText: true };
   401→
   402→            const colorBlue = "4472C4";
   403→            const colorGreen = "548235";
   404→            const colorRed = "C00000";
   405→            const colorYellow = "FFC000";
   406→            const colorWhite = "FFFFFF";
   407→
   408→            // Helper to get range
   409→            const range = XLSX.utils.decode_range(ws['!ref']);
   410→
   411→            for (let R = range.s.r; R <= range.e.r; ++R) {
   412→                for (let C = range.s.c; C <= range.e.c; ++C) {
   413→                    const cell_address = XLSX.utils.encode_cell({ r: R, c: C });
   414→                    if (!ws[cell_address]) continue;
   415→
   416→                    // Base Style
   417→                    let cellStyle = {
   418→                        border: borderStyle,
   419→                        alignment: alignCenter,
   420→                        font: { name: "Calibri", sz: 11 }
   421→                    };
   422→
   423→                    // Header Row Styles (Row 0)
   424→                    if (R === 0) {
   425→                        cellStyle.font.bold = true;
   426→                        cellStyle.font.color = { rgb: colorWhite };
   427→
   428→                        let bgColor = null;
   429→                        if (C <= 3) bgColor = colorBlue;      // 0,1,2,3 (Series, Scale, Dur, DurChg)
   430→                        else if (C <= 5) bgColor = colorGreen; // 4,5 (Lev, LevChg)
   431→                        else if (C <= 7) bgColor = colorRed;   // 6,7 (Eq, EqChg)
   432→                        else if (C <= 9) bgColor = colorYellow;// 8,9 (Der, DerChg)
   433→
   434→                        if (bgColor) {
   435→                            cellStyle.fill = { fgColor: { rgb: bgColor } };
   436→                        }
   437→                    } else {
   438→                        // Body Row Styles
   439→                        // Column 0: Bold
   440→                        if (C === 0) {
   441→                            cellStyle.font.bold = true;
   442→                        }
   443→
   444→                        // Change Columns (3, 5, 7, 9): Red Text + Bold
   445→                        if ([3, 5, 7, 9].includes(C)) {
   446→                            cellStyle.font.color = { rgb: colorRed };
   447→                            cellStyle.font.bold = true;
   448→                        }
   449→                    }
   450→
   451→                    ws[cell_address].s = cellStyle;
   452→                }
   453→            }
   454→
   455→            // Adjust Column Widths
   456→            ws['!cols'] = [
   457→                { wch: 15 }, // Series
   458→                { wch: 12 }, // Scale
   459→                { wch: 12 }, // Dur
   460→                { wch: 15 }, // DurChg
   461→                { wch: 12 }, // Lev
   462→                { wch: 15 }, // LevChg
   463→                { wch: 12 }, // Eq
   464→                { wch: 15 }, // EqChg
   465→                { wch: 15 }, // Der
   466→                { wch: 15 }  // DerChg
   467→            ];
   468→
   469→            const wb = XLSX.utils.book_new();
   470→            XLSX.utils.book_append_sheet(wb, ws, "产品运作概览");
   471→
   472→            // Generate filename with timestamp
   473→            const date = new Date();
   474→            const dateStr = date.toISOString().split('T')[0];
   475→            XLSX.writeFile(wb, `产品运作概览信息表_${dateStr}.xlsx`);
   476→        }
   477→    </script>
   478→</body>
