   501→        <!-- Upload Section -->
   502→        <section class="upload-section">
   503→            <div class="upload-grid">
   504→                <div class="upload-box">
   505→                    <label class="upload-label">当期产品运作表 (Excel)</label>
   506→                    <div class="file-input-wrapper">
   507→                        <input type="file" id="currentFile" accept=".xlsx, .xls">
   508→                        <div class="file-input-icon">📊</div>
   509→                        <div class="file-input-text">点击选择文件或拖拽到此处</div>
   510→                    </div>
   511→                </div>
   512→                <div class="upload-box">
   513→                    <label class="upload-label">对比期产品运作表 (Excel)</label>
   514→                    <div class="file-input-wrapper">
   515→                        <input type="file" id="compareFile" accept=".xlsx, .xls">
   516→                        <div class="file-input-icon">📈</div>
   517→                        <div class="file-input-text">点击选择文件或拖拽到此处</div>
   518→                    </div>
