   540→
   541→    <script>
   542→        // Configuration
   543→        const MAPPING = [
   544→            { key: '悦动稳享短持', name: '稳享' },
   545→            { key: '丰利/增盈', name: '合享' },
   546→            { key: '逸动短持', name: '逸动' },
   547→            { key: '悦动短持/定开', name: '悦动' },
   548→            { key: '灵动短持', name: '灵动' },
   549→            { key: '兴动短持', name: '兴动' }
   550→        ];
   551→
   552→        const COLS = {
   553→            CATEGORY: '母分类',
   554→            NET_ASSETS: '净资产(亿)',
   555→            DURATION: '组合久期',
   556→            LEVERAGE: '杠杆',
   557→            EQUITY: '权益仓位',
   558→            DERIVATIVES: '衍生品仓位',
   559→            FLAG: '母子标志'
   560→        };
   561→
   562→        let generatedData = [];
   563→
   564→        async function readExcel(file) {
   565→            return new Promise((resolve, reject) => {
   566→                const reader = new FileReader();
   567→                reader.onload = (e) => {
   568→                    try {
   569→                        const data = new Uint8Array(e.target.result);
   570→                        const workbook = XLSX.read(data, { type: 'array' });
   571→                        const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
   572→                        const jsonData = XLSX.utils.sheet_to_json(firstSheet);
   573→
   574→                        const normalizedData = jsonData.map(row => {
   575→                            const newRow = {};
   576→                            Object.keys(row).forEach(key => {
   577→                                newRow[key.trim()] = row[key];
   578→                            });
   579→                            return newRow;
   580→                        });
   581→
   582→                        resolve(normalizedData);
   583→                    } catch (err) {
   584→                        reject(err);
   585→                    }
   586→                };
   587→                reader.onerror = reject;
   588→                reader.readAsArrayBuffer(file);
   589→            });
   590→        }
   591→
   592→        function extractDateFromFilename(filename) {
   593→            const match = filename.match(/(\d{4}[-年]?\d{2}[-月]?\d{2})/);
   594→            return match ? match[0] : '当期';
   595→        }
   596→
   597→        async function processFiles() {
   598→            const currentFile = document.getElementById('currentFile').files[0];
   599→            const compareFile = document.getElementById('compareFile').files[0];
   600→
   601→            if (!currentFile || !compareFile) {
   602→                alert("请上传两个文件！");
   603→                return;
   604→            }
   605→
   606→            try {
   607→                let currentData = await readExcel(currentFile);
   608→                let compareData = await readExcel(compareFile);
   609→
   610→                const filterData = (data) => {
   611→                    return data.filter(row => {
   612→                        const flag = row[COLS.FLAG];
   613→                        if (!flag) return false;
   614→                        const val = String(flag).trim();
   615→                        return val === '母产品' || val === '非母子产品';
   616→                    });
   617→                };
   618→
   619→                currentData = filterData(currentData);
   620→                compareData = filterData(compareData);
   621→
   622→                const currentDate = extractDateFromFilename(currentFile.name);
   623→                const compareDate = extractDateFromFilename(compareFile.name);
   624→
   625→                generateDashboard(currentData, compareData, currentDate, compareDate);
   626→                document.getElementById('exportBtn').classList.remove('hidden');
   627→
   628→                window.currentDateStr = currentDate;
   629→                window.compareDateStr = compareDate;
   630→            } catch (error) {
   631→                console.error(error);
   632→                alert("处理文件时出错: " + error.message);
   633→            }
   634→        }
   635→
   636→        const parseNum = (val) => {
   637→            if (typeof val === 'number') return val;
   638→            if (typeof val === 'string') {
   639→                val = val.trim();
   640→                if (val.endsWith('%')) {
   641→                    return parseFloat(val) / 100;
   642→                }
   643→                return parseFloat(val);
   644→            }
   645→            return 0;
   646→        };
   647→
   648→        function calculateTotalScale(data, categoryKey) {
   649→            const items = data.filter(row => row[COLS.CATEGORY] === categoryKey);
   650→            let totalAssets = 0;
   651→            items.forEach(row => {
   652→                let assets = row[COLS.NET_ASSETS];
   653→                assets = parseNum(assets) || 0;
   654→                totalAssets += assets;
   655→            });
   656→            return totalAssets;
   657→        }
   658→
   659→        function calculateWeightedAvg(data, categoryKey, metricKey) {
   660→            const items = data.filter(row => row[COLS.CATEGORY] === categoryKey);
   661→            if (items.length === 0) return null;
   662→
   663→            let totalWeighted = 0;
   664→            let totalAssets = 0;
   665→
   666→            items.forEach(row => {
   667→                let assets = row[COLS.NET_ASSETS];
   668→                let metric = row[metricKey];
   669→
   670→                assets = parseNum(assets) || 0;
   671→                metric = parseNum(metric) || 0;
   672→
   673→                totalWeighted += metric * assets;
   674→                totalAssets += assets;
   675→            });
   676→
   677→            return totalAssets === 0 ? 0 : totalWeighted / totalAssets;
   678→        }
   679→
   680→        function formatValue(value, type) {
   681→            if (value === null || value === undefined || isNaN(value)) return '-';
   682→            if (Math.abs(value) < 0.000001) return '-';
   683→
   684→            if (type === 'percent') {
   685→                return (value * 100).toFixed(2) + '%';
   686→            } else if (type === 'percent_int') {
   687→                return (value * 100).toFixed(0) + '%';
   688→            } else if (type === 'decimal') {
   689→                return value.toFixed(2);
   690→            }
   691→            return value;
   692→        }
