   167→<body class="p-8">
   168→
   169→    <div class="max-w-7xl mx-auto">
   170→        <div class="bg-white p-8 rounded-xl shadow-sm mb-8">
   171→            <h1 class="text-3xl font-bold mb-2 text-gray-800">系列产品运作情况统计工具</h1>
   172→            <p class="text-gray-500 text-sm">Multi-Asset Investment Department Performance Analysis</p>
   173→        </div>
   174→
   175→        <div class="bg-white p-6 rounded-xl shadow-sm mb-8">
   176→            <div class="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
   177→                <div>
   178→                    <label class="block text-sm font-semibold text-gray-700 mb-2">当期产品运作表 (Excel)</label>
   179→                    <input type="file" id="currentFile" accept=".xlsx, .xls"
   180→                        class="block w-full text-sm text-gray-600 file:mr-4 file:py-2.5 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100 file:transition-colors" />
   181→                </div>
   182→                <div>
   183→                    <label class="block text-sm font-semibold text-gray-700 mb-2">对比期产品运作表 (Excel)</label>
   184→                    <input type="file" id="compareFile" accept=".xlsx, .xls"
   185→                        class="block w-full text-sm text-gray-600 file:mr-4 file:py-2.5 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100 file:transition-colors" />
   186→                </div>
   187→            </div>
   188→
   189→            <div class="flex gap-4 justify-center">
   190→                <button onclick="processFiles()" class="btn-primary">📊 生成统计表</button>
   191→                <button onclick="exportToExcel()" id="exportBtn" class="btn-success hidden">📥 导出 Excel</button>
   192→            </div>
   193→        </div>
   194→
   195→        <div id="resultContainer" class="overflow-x-auto">
   196→            <!-- Table will be inserted here -->
   197→        </div>
   198→    </div>
   199→
   200→    <script>
   201→        // Configuration
   202→        const MAPPING = [
   203→            { key: '悦动稳享短持', name: '稳享' },
   204→            { key: '丰利/增盈', name: '合享' },
   205→            { key: '逸动短持', name: '逸动' },
   206→            { key: '悦动短持/定开', name: '悦动' },
   207→            { key: '灵动短持', name: '灵动' },
   208→            { key: '兴动短持', name: '兴动' }
   209→        ];
   210→
   211→        // Expected Columns
   212→        const COLS = {
   213→            CATEGORY: '母分类',
   214→            NET_ASSETS: '净资产(亿)',
   215→            DURATION: '组合久期',
   216→            LEVERAGE: '杠杆',
   217→            EQUITY: '权益仓位',
   218→            DERIVATIVES: '衍生品仓位',
   219→            FLAG: '母子标志'
   220→        };
   221→
   222→        let generatedData = [];
   223→
   224→        async function readExcel(file) {
   225→            return new Promise((resolve, reject) => {
   226→                const reader = new FileReader();
   227→                reader.onload = (e) => {
   228→                    try {
   229→                        const data = new Uint8Array(e.target.result);
   230→                        const workbook = XLSX.read(data, { type: 'array' });
   231→                        const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
   232→                        const jsonData = XLSX.utils.sheet_to_json(firstSheet);
   233→
   234→                        // Normalize keys (trim spaces)
   235→                        const normalizedData = jsonData.map(row => {
   236→                            const newRow = {};
   237→                            Object.keys(row).forEach(key => {
   238→                                newRow[key.trim()] = row[key];
   239→                            });
   240→                            return newRow;
   241→                        });
   242→
   243→                        resolve(normalizedData);
   244→                    } catch (err) {
   245→                        reject(err);
   246→                    }
   247→                };
   248→                reader.onerror = reject;
   249→                reader.readAsArrayBuffer(file);
   250→            });
   251→        }
   252→
   253→        function extractDateFromFilename(filename) {
   254→            // Match YYYY-MM-DD or YYYYMMDD
   255→            const match = filename.match(/(\d{4}[-年]?\d{2}[-月]?\d{2})/);
   256→            return match ? match[0] : '当期';
   257→        }
   258→
   259→        async function processFiles() {
   260→            const currentFile = document.getElementById('currentFile').files[0];
   261→            const compareFile = document.getElementById('compareFile').files[0];
   262→
   263→            if (!currentFile || !compareFile) {
   264→                alert("请上传两个文件！");
   265→                return;
   266→            }
   267→
   268→            try {
   269→                let currentData = await readExcel(currentFile);
   270→                let compareData = await readExcel(compareFile);
   271→
   272→                const filterData = (data) => {
   273→                    return data.filter(row => {
   274→                        const flag = row[COLS.FLAG];
   275→                        if (!flag) return false;
   276→                        const val = String(flag).trim();
   277→                        return val === '母产品' || val === '非母子产品';
   278→                    });
   279→                };
   280→
   281→                currentData = filterData(currentData);
   282→                compareData = filterData(compareData);
   283→
   284→                const currentDate = extractDateFromFilename(currentFile.name);
   285→                const compareDate = extractDateFromFilename(compareFile.name);
   286→
   287→                generateTable(currentData, compareData, currentDate, compareDate);
   288→                document.getElementById('exportBtn').classList.remove('hidden');
   289→
   290→                // Store dates for export
   291→                window.currentDateStr = currentDate;
   292→                window.compareDateStr = compareDate;
   293→            } catch (error) {
   294→                console.error(error);
   295→                alert("处理文件时出错: " + error.message);
   296→            }
   297→        }
   298→
   299→        const parseNum = (val) => {
   300→            if (typeof val === 'number') return val;
   301→            if (typeof val === 'string') {
   302→                val = val.trim();
   303→                if (val.endsWith('%')) {
   304→                    return parseFloat(val) / 100;
   305→                }
   306→                return parseFloat(val);
   307→            }
   308→            return 0;
   309→        };
   310→
   311→        function calculateTotalScale(data, categoryKey) {
   312→            const items = data.filter(row => row[COLS.CATEGORY] === categoryKey);
   313→            let totalAssets = 0;
   314→            items.forEach(row => {
   315→                let assets = row[COLS.NET_ASSETS];
   316→                assets = parseNum(assets) || 0;
   317→                totalAssets += assets;
   318→            });
   319→            return totalAssets;
   320→        }
   321→
   322→        function calculateWeightedAvg(data, categoryKey, metricKey) {
   323→            const items = data.filter(row => row[COLS.CATEGORY] === categoryKey);
   324→            if (items.length === 0) return null;
   325→
   326→            let totalWeighted = 0;
   327→            let totalAssets = 0;
   328→
   329→            items.forEach(row => {
   330→                let assets = row[COLS.NET_ASSETS];
   331→                let metric = row[metricKey];
   332→
   333→                assets = parseNum(assets) || 0;
   334→                metric = parseNum(metric) || 0;
   335→
   336→                totalWeighted += metric * assets;
   337→                totalAssets += assets;
   338→            });
   339→
   340→            return totalAssets === 0 ? 0 : totalWeighted / totalAssets;
   341→        }
   342→
   343→        function formatValue(value, type) {
   344→            if (value === null || value === undefined || isNaN(value)) return '-';
   345→            if (Math.abs(value) < 0.000001) return '-';
   346→
   347→            if (type === 'percent') {
   348→                return (value * 100).toFixed(2) + '%';
   349→            } else if (type === 'percent_int') {
   350→                return (value * 100).toFixed(0) + '%';
   351→            } else if (type === 'decimal') {
   352→                return value.toFixed(2);
   353→            }
   354→            return value;
   355→        }
   356→
   357→        function getChangeBadge(change, type) {
   358→            if (change === null || change === undefined || isNaN(change) || Math.abs(change) < 0.000001) {
   359→                return '';
   360→            }
   361→
   362→            const formattedChange = formatValue(Math.abs(change), type);
   363→            const isPositive = change > 0;
   364→            const arrow = isPositive ? '↑' : '↓';
   365→            const sign = isPositive ? '+' : '';
   366→            const className = isPositive ? 'up' : 'down';
   367→
   368→            return `<span class="change-badge ${className}"><span class="arrow">${arrow}</span>${sign}${formattedChange}</span>`;
   369→        }
   370→
   371→        function generateTable(currentData, compareData, currentDate, compareDate) {
   372→            let html = `
   373→                <div class="table-container">
   374→                    <table class="custom-table" id="resultTable">
   375→                        <thead>
   376→                            <tr>
   377→                                <th class="header-blue">产品系列</th>
   378→                                <th class="header-blue">规模(亿)</th>
   379→                                <th class="header-blue">${currentDate}久期</th>
   380→                                <th class="header-green">${currentDate}杠杆</th>
   381→                                <th class="header-red">${currentDate}权益仓位</th>
   382→                                <th class="header-yellow">${currentDate}衍生品仓位</th>
   383→                            </tr>
   384→                        </thead>
   385→                        <tbody>
   386→            `;
   387→
   388→            generatedData = []; // Reset for export
   389→
   390→            MAPPING.forEach(item => {
   391→                const rowData = { series: item.name };
   392→
   393→                // Scale
   394→                rowData.scale = calculateTotalScale(currentData, item.key);
   395→
   396→                // Duration
   397→                const curDur = calculateWeightedAvg(currentData, item.key, COLS.DURATION);
   398→                const oldDur = calculateWeightedAvg(compareData, item.key, COLS.DURATION);
   399→                rowData.dur = curDur;
   400→                rowData.durChg = (curDur !== null && oldDur !== null) ? curDur - oldDur : null;
   401→
   402→                // Leverage
   403→                const curLev = calculateWeightedAvg(currentData, item.key, COLS.LEVERAGE);
   404→                const oldLev = calculateWeightedAvg(compareData, item.key, COLS.LEVERAGE);
   405→                rowData.lev = curLev;
   406→                rowData.levChg = (curLev !== null && oldLev !== null) ? curLev - oldLev : null;
   407→
   408→                // Equity - 稳享和悦动系列没有权益仓位
   409→                let curEq = null;
   410→                let oldEq = null;
   411→                if (item.name !== '稳享' && item.name !== '悦动') {
   412→                    curEq = calculateWeightedAvg(currentData, item.key, COLS.EQUITY);
   413→                    oldEq = calculateWeightedAvg(compareData, item.key, COLS.EQUITY);
   414→                }
   415→                rowData.eq = curEq;
   416→                rowData.eqChg = (curEq !== null && oldEq !== null) ? curEq - oldEq : null;
   417→
   418→                // Derivatives
   419→                const curDer = calculateWeightedAvg(currentData, item.key, COLS.DERIVATIVES);
   420→                const oldDer = calculateWeightedAvg(compareData, item.key, COLS.DERIVATIVES);
   421→                rowData.der = curDer;
   422→                rowData.derChg = (curDer !== null && oldDer !== null) ? curDer - oldDer : null;
   423→
   424→                generatedData.push(rowData);
   425→
   426→                html += `
   427→                    <tr>
   428→                        <td class="series-name">${item.name}</td>
   429→                        <td class="value-cell">${formatValue(rowData.scale, 'decimal')}</td>
   430→                        <td class="value-cell">
   431→                            ${formatValue(rowData.dur, 'decimal')}
   432→                            ${getChangeBadge(rowData.durChg, 'decimal')}
   433→                        </td>
   434→                        <td class="value-cell">
   435→                            ${formatValue(rowData.lev, 'percent_int')}
   436→                            ${getChangeBadge(rowData.levChg, 'percent_int')}
   437→                        </td>
   438→                        <td class="value-cell">
   439→                            ${formatValue(rowData.eq, 'percent')}
   440→                            ${getChangeBadge(rowData.eqChg, 'percent')}
   441→                        </td>
   442→                        <td class="value-cell">
   443→                            ${formatValue(rowData.der, 'percent')}
   444→                            ${getChangeBadge(rowData.derChg, 'percent')}
   445→                        </td>
   446→                    </tr>
   447→                `;
   448→            });
   449→
   450→            html += `</tbody></table></div>`;
   451→            document.getElementById('resultContainer').innerHTML = html;
   452→        }
   453→
   454→        function exportToExcel() {
   455→            const currentDate = window.currentDateStr || '当期';
   456→            const compareDate = window.compareDateStr || '对比期';
   457→            const diffLabel = `（相比于${compareDate}）`;
   458→
   459→            // Data prep - 导出完整数据包含变化列
   460→            const wsData = [
   461→                [
   462→                    "产品系列",
   463→                    "规模(亿)",
   464→                    `${currentDate}久期`, `久期变化${diffLabel}`,
   465→                    `${currentDate}杠杆`, `杠杆变化${diffLabel}`,
   466→                    `${currentDate}权益仓位`, `权益仓位变化${diffLabel}`,
   467→                    `${currentDate}衍生品仓位`, `衍生品仓位变化${diffLabel}`
   468→                ]
   469→            ];
   470→
   471→            generatedData.forEach(row => {
   472→                wsData.push([
   473→                    row.series,
   474→                    formatValue(row.scale, 'decimal'),
   475→                    formatValue(row.dur, 'decimal'),
   476→                    formatValue(row.durChg, 'decimal'),
   477→                    formatValue(row.lev, 'percent_int'),
   478→                    formatValue(row.levChg, 'percent_int'),
   479→                    formatValue(row.eq, 'percent'),
   480→                    formatValue(row.eqChg, 'percent'),
   481→                    formatValue(row.der, 'percent'),
   482→                    formatValue(row.derChg, 'percent')
   483→                ]);
   484→            });
   485→
   486→            // Create Sheet
   487→            const ws = XLSX.utils.aoa_to_sheet(wsData);
   488→
   489→            // Define Styles
   490→            const borderStyle = { top: { style: "thin" }, bottom: { style: "thin" }, left: { style: "thin" }, right: { style: "thin" } };
   491→            const alignCenter = { horizontal: "center", vertical: "center", wrapText: true };
   492→
   493→            const colorBlue = "4472C4";
   494→            const colorGreen = "548235";
   495→            const colorRed = "C00000";
   496→            const colorYellow = "FFC000";
   497→            const colorWhite = "FFFFFF";
   498→
   499→            // Helper to get range
   500→            const range = XLSX.utils.decode_range(ws['!ref']);
   501→
   502→            for (let R = range.s.r; R <= range.e.r; ++R) {
   503→                for (let C = range.s.c; C <= range.e.c; ++C) {
   504→                    const cell_address = XLSX.utils.encode_cell({ r: R, c: C });
   505→                    if (!ws[cell_address]) continue;
   506→
   507→                    // Base Style
   508→                    let cellStyle = {
   509→                        border: borderStyle,
   510→                        alignment: alignCenter,
   511→                        font: { name: "Calibri", sz: 11 }
   512→                    };
   513→
   514→                    // Header Row Styles (Row 0)
   515→                    if (R === 0) {
   516→                        cellStyle.font.bold = true;
   517→                        cellStyle.font.color = { rgb: colorWhite };
   518→
   519→                        let bgColor = null;
   520→                        if (C <= 3) bgColor = colorBlue;      // 0,1,2,3 (Series, Scale, Dur, DurChg)
   521→                        else if (C <= 5) bgColor = colorGreen; // 4,5 (Lev, LevChg)
   522→                        else if (C <= 7) bgColor = colorRed;   // 6,7 (Eq, EqChg)
   523→                        else if (C <= 9) bgColor = colorYellow;// 8,9 (Der, DerChg)
   524→
   525→                        if (bgColor) {
   526→                            cellStyle.fill = { fgColor: { rgb: bgColor } };
   527→                        }
   528→                    } else {
   529→                        // Body Row Styles
   530→                        // Column 0: Bold
   531→                        if (C === 0) {
   532→                            cellStyle.font.bold = true;
   533→                        }
   534→
   535→                        // Change Columns (3, 5, 7, 9): Red Text + Bold
   536→                        if ([3, 5, 7, 9].includes(C)) {
   537→                            cellStyle.font.color = { rgb: colorRed };
   538→                            cellStyle.font.bold = true;
   539→                        }
   540→                    }
   541→
   542→                    ws[cell_address].s = cellStyle;
   543→                }
   544→            }
   545→
   546→            // Adjust Column Widths
   547→            ws['!cols'] = [
   548→                { wch: 15 }, // Series
   549→                { wch: 12 }, // Scale
   550→                { wch: 12 }, // Dur
   551→                { wch: 15 }, // DurChg
   552→                { wch: 12 }, // Lev
   553→                { wch: 15 }, // LevChg
   554→                { wch: 12 }, // Eq
   555→                { wch: 15 }, // EqChg
   556→                { wch: 15 }, // Der
   557→                { wch: 15 }  // DerChg
   558→            ];
   559→
   560→            const wb = XLSX.utils.book_new();
   561→            XLSX.utils.book_append_sheet(wb, ws, "产品运作概览");
   562→
   563→            // Generate filename with timestamp
   564→            const date = new Date();
   565→            const dateStr = date.toISOString().split('T')[0];
   566→            XLSX.writeFile(wb, `产品运作概览信息表_${dateStr}.xlsx`);
   567→        }
   568→    </script>
   569→</body>
   570→
   571→</html>

