                if (C <= 3) bgColor = colorBlue;      // 0,1,2,3 (Series, Scale, Dur, DurChg)
                else if (C <= 5) bgColor = colorGreen; // 4,5 (Lev, LevChg)
                else if (C <= 7) bgColor = colorRed;   // 6,7 (Eq, EqChg)
                else if (C <= 9) bgColor = colorYellow;// 8,9 (Der, DerChg)

                if (bgColor) {
                    cellStyle.fill = { fgColor: { rgb: bgColor } };
                }
            } else {
                // Body Row Styles
                // Column 0: Bold
                if (C === 0) {
                    cellStyle.font.bold = true;
                }

                // Change Columns (3, 5, 7, 9): Red Text + Bold
                if ([3, 5, 7, 9].includes(C)) {
                    cellStyle.font.color = { rgb: colorRed };
                    cellStyle.font.bold = true;
                }
            }

            ws[cell_address].s = cellStyle;
        }
    }

    // Adjust Column Widths
    ws['!cols'] = [
        { wch: 15 }, // Series
        { wch: 12 }, // Scale
        { wch: 12 }, // Dur
        { wch: 15 }, // DurChg
        { wch: 12 }, // Lev
        { wch: 15 }, // LevChg
        { wch: 12 }, // Eq
        { wch: 15 }, // EqChg
        { wch: 15 }, // Der
        { wch: 15 }  // DerChg
    ];

    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "产品运作概览");

    // Generate filename with timestamp
