            formatValue(row.eq, 'percent') + getExcelChangeText(row.eqChg, 'percent'),
            formatValue(row.der, 'percent') + getExcelChangeText(row.derChg, 'percent')
        ]);
    });

    // Create Sheet
    const ws = XLSX.utils.aoa_to_sheet(wsData);

    // Define Styles
    const borderStyle = { top: { style: "thin" }, bottom: { style: "thin" }, left: { style: "thin" }, right: { style: "thin" } };
    const alignCenter = { horizontal: "center", vertical: "center", wrapText: true };

    const colorBlue = "4472C4";
    const colorGreen = "548235";
    const colorRed = "C00000";
    const colorYellow = "FFC000";
    const colorWhite = "FFFFFF";

    // Helper to get range
    const range = XLSX.utils.decode_range(ws['!ref']);

    for (let R = range.s.r; R <= range.e.r; ++R) {
        for (let C = range.s.c; C <= range.e.c; ++C) {
            const cell_address = XLSX.utils.encode_cell({ r: R, c: C });
            if (!ws[cell_address]) continue;

            // Base Style
            let cellStyle = {
                border: borderStyle,
                alignment: alignCenter,
                font: { name: "Calibri", sz: 11 }
            };

            // Header Row Styles (Row 0)
            if (R === 0) {
                cellStyle.font.bold = true;
                cellStyle.font.color = { rgb: colorWhite };

                let bgColor = null;
                if (C === 0 || C === 1) bgColor = colorBlue;      // 产品系列, 规模
                else if (C === 2) bgColor = colorBlue;             // 久期
                else if (C === 3) bgColor = colorGreen;            // 杠杆
                else if (C === 4) bgColor = colorRed;              // 权益仓位
                else if (C === 5) bgColor = colorYellow;           // 衍生品仓位

                if (bgColor) {
                    cellStyle.fill = { fgColor: { rgb: bgColor } };
                }
            } else {
                // Body Row Styles
                const dataRowIndex = R - 1; // 数据行索引

                if (C === 0) {
                    // 产品系列列：加粗
                    cellStyle.font.bold = true;
                } else if (C >= 2 && C <= 5 && dataRowIndex < changeInfo.length) {
                    // 有变化信息的列，需要使用富文本
                    const changes = changeInfo[dataRowIndex];
                    let changeValue = null;

                    if (C === 2) changeValue = changes.dur;
                    else if (C === 3) changeValue = changes.lev;
                    else if (C === 4) changeValue = changes.eq;
                    else if (C === 5) changeValue = changes.der;

                    if (changeValue !== null && changeValue !== undefined && !isNaN(changeValue) && Math.abs(changeValue) >= 0.000001) {
                        // 有变化：创建富文本
                        const cellValue = ws[cell_address].v;
                        const parts = cellValue.split('\n');
                        const mainValue = parts[0];
                        const changeText = parts[1] || '';

                        if (changeText) {
                            const isPositive = changeValue > 0;
                            const changeColor = isPositive ? colorRed : colorGreen; // 增加用红色，减少用绿色

                            ws[cell_address].t = 'n'; // 设为数值类型避免富文本问题
                            ws[cell_address].z = '#,##0.00'; // 数字格式

                            // 添加批注显示变化
                            ws[cell_address].c = [{
                                a: "变化",
                                t: changeText
                            }];

                            // 根据变化方向设置字体颜色
                            cellStyle.font.color = { rgb: changeColor };
                            cellStyle.font.bold = true;
                        }
                    }
                }
            }

            ws[cell_address].s = cellStyle;
        }
    }

    // Adjust Column Widths
    ws['!cols'] = [
        { wch: 15 }, // Series
        { wch: 12 }, // Scale
        { wch: 16 }, // Dur
        { wch: 14 }, // Lev
        { wch: 16 }, // Eq
        { wch: 18 }  // Der
    ];

    // Set row heights for better display
    ws['!rows'] = [];
    for (let i = 0; i <= generatedData.length; i++) {
        ws['!rows'][i] = { hpt: i === 0 ? 30 : 35 };
    }

    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "产品运作概览");

    // Generate filename with timestamp
    const date = new Date();
    const dateStr = date.toISOString().split('T')[0];
    XLSX.writeFile(wb, `产品运作概览信息表_${dateStr}.xlsx`);
}

