   487→            const isPositive = change > 0;
   488→            const arrow = isPositive ? '↑' : '↓';
   489→            const sign = isPositive ? '+' : '';
   490→            const className = isPositive ? 'up' : 'down';
   491→
   492→            return `<span class="change-badge ${className}"><span class="arrow">${arrow}</span>${sign}${formattedChange}</span>`;
   493→        }
   494→
   495→        function generateTable(currentData, compareData, currentDate, compareDate) {
   496→            let html = `
   497→                <div class="table-container">
   498→                    <table class="custom-table" id="resultTable">
   499→                        <thead>
   500→                            <tr>
   501→                                <th class="header-blue">产品系列</th>
   502→                                <th class="header-blue">规模(亿)</th>
   503→                                <th class="header-blue">${currentDate}久期</th>
   504→                                <th class="header-green">${currentDate}杠杆</th>
   505→                                <th class="header-red">${currentDate}权益仓位</th>
   506→                                <th class="header-yellow">${currentDate}衍生品仓位</th>
   507→                                <th class="header-purple">20241231黄金及商品仓位</th>
   508→                            </tr>
   509→                        </thead>
   510→                        <tbody>
   511→            `;
   512→
   513→            generatedData = [];
   514→
   515→            MAPPING.forEach((item, index) => {
   516→                const rowData = { series: item.name };
   517→
   518→                // Scale
   519→                rowData.scale = calculateTotalScale(currentData, item.key);
   520→
   521→                // Duration
   522→                const curDur = calculateWeightedAvg(currentData, item.key, COLS.DURATION);
   523→                const oldDur = calculateWeightedAvg(compareData, item.key, COLS.DURATION);
   524→                rowData.dur = curDur;
   525→                rowData.durChg = (curDur !== null && oldDur !== null) ? curDur - oldDur : null;
   526→
   527→                // Leverage
   528→                const curLev = calculateWeightedAvg(currentData, item.key, COLS.LEVERAGE);
   529→                const oldLev = calculateWeightedAvg(compareData, item.key, COLS.LEVERAGE);
   530→                rowData.lev = curLev;
   531→                rowData.levChg = (curLev !== null && oldLev !== null) ? curLev - oldLev : null;
   532→
   533→                // Equity - 稳享和悦动系列没有权益仓位
   534→                let curEq = null;
   535→                let oldEq = null;
   536→                if (item.name !== '稳享' && item.name !== '悦动') {
   537→                    curEq = calculateWeightedAvg(currentData, item.key, COLS.EQUITY);
   538→                    oldEq = calculateWeightedAvg(compareData, item.key, COLS.EQUITY);
   539→                }
   540→                rowData.eq = curEq;
   541→                rowData.eqChg = (curEq !== null && oldEq !== null) ? curEq - oldEq : null;
   542→
   543→                // Derivatives
   544→                const curDer = calculateWeightedAvg(currentData, item.key, COLS.DERIVATIVES);
   545→                const oldDer = calculateWeightedAvg(compareData, item.key, COLS.DERIVATIVES);
   546→                rowData.der = curDer;
   547→                rowData.derChg = (curDer !== null && oldDer !== null) ? curDer - oldDer : null;
   548→
   549→                generatedData.push(rowData);
   550→
   551→                const commodityCell = index === 0
   552→                    ? `<td class="commodity-cell" rowspan="${MAPPING.length}" id="commodityDisplayCell">-</td>`
   553→                    : '';
   554→
   555→                html += `
   556→                    <tr>
   557→                        <td class="series-name">${item.name}</td>
   558→                        <td class="value-cell">${formatValue(rowData.scale, 'integer')}</td>
   559→                        <td class="value-cell">
   560→                            ${formatValue(rowData.dur, 'decimal')}
   561→                            ${getChangeBadge(rowData.durChg, 'decimal')}
   562→                        </td>
   563→                        <td class="value-cell">
   564→                            ${formatValue(rowData.lev, 'percent_int')}
   565→                            ${getChangeBadge(rowData.levChg, 'percent_int')}
   566→                        </td>
   567→                        <td class="value-cell">
   568→                            ${formatValue(rowData.eq, 'percent')}
   569→                            ${getChangeBadge(rowData.eqChg, 'percent')}
   570→                        </td>
   571→                        <td class="value-cell">
   572→                            ${formatValue(rowData.der, 'percent')}
   573→                            ${getChangeBadge(rowData.derChg, 'percent')}
   574→                        </td>
   575→                        ${commodityCell}
   576→                    </tr>
   577→                `;
   578→            });
   579→
   580→            html += `</tbody></table></div>`;
   581→            document.getElementById('resultContainer').innerHTML = html;
   582→        }
   583→
   584→        function getExcelChangeText(change, type) {
   585→            if (change === null || change === undefined || isNaN(change) || Math.abs(change) < 0.000001) {
   586→                return '';
   587→            }
   588→            const formattedChange = formatValue(Math.abs(change), type);
   589→            const isPositive = change > 0;
   590→            const arrow = isPositive ? '↑' : '↓';
   591→            const sign = isPositive ? '+' : '';
   592→            return `\n${arrow}${sign}${formattedChange}`;
   593→        }
   594→
   595→        function exportToExcel() {
   596→            const currentDate = window.currentDateStr || '当期';
   597→
   598→            const wb = XLSX.utils.book_new();
   599→
   600→            // 创建工作表数据
   601→            const wsData = [
   602→                [
   603→                    "产品系列",
   604→                    "规模(亿)",
   605→                    `${currentDate}久期`,
   606→                    `${currentDate}杠杆`,
   607→                    `${currentDate}权益仓位`,
   608→                    `${currentDate}衍生品仓位`,
   609→                    "20241231黄金及商品仓位"
   610→                ]
   611→            ];
   612→
   613→            const changeInfo = [];
   614→
   615→            generatedData.forEach(row => {
   616→                changeInfo.push({
   617→                    dur: row.durChg,
   618→                    lev: row.levChg,
   619→                    eq: row.eqChg,
   620→                    der: row.derChg
   621→                });
   622→
   623→                wsData.push([
   624→                    row.series,
   625→                    formatValue(row.scale, 'integer'),
   626→                    formatValue(row.dur, 'decimal') + getExcelChangeText(row.durChg, 'decimal'),
   627→                    formatValue(row.lev, 'percent_int') + getExcelChangeText(row.levChg, 'percent_int'),
   628→                    formatValue(row.eq, 'percent') + getExcelChangeText(row.eqChg, 'percent'),
   629→                    formatValue(row.der, 'percent') + getExcelChangeText(row.derChg, 'percent'),
   630→                    row.series === '稳享'
   631→                        ? formatValue(commodityData.current, 'percent') + getExcelChangeText(commodityData.change, 'percent')
   632→                        : ''
   633→                ]);
