   640→            ws['!merges'].push({
   641→                s: { r: 1, c: 6 },  // 开始：第2行，第7列
   642→                e: { r: MAPPING.length, c: 6 }  // 结束：最后一行，第7列
   643→            });
   644→
   645→            const borderStyle = {
   646→                top: { style: "thin" },
   647→                bottom: { style: "thin" },
   648→                left: { style: "thin" },
   649→                right: { style: "thin" }
   650→            };
   651→            const alignCenter = {
