   653→                vertical: "center",
   654→                wrapText: true
   655→            };
   656→
   657→            const colorBlue = "4472C4";
   658→            const colorGreen = "548235";
   659→            const colorRed = "C00000";
   660→            const colorYellow = "FFC000";
   661→            const colorPurple = "7030A0";
   662→            const colorWhite = "FFFFFF";
   663→
   664→            const range = XLSX.utils.decode_range(ws['!ref']);
   665→
   666→            for (let R = range.s.r; R <= range.e.r; ++R) {
   667→                for (let C = range.s.c; C <= range.e.c; ++C) {
   668→                    const cell_address = XLSX.utils.encode_cell({ r: R, c: C });
   669→                    if (!ws[cell_address]) continue;
   670→
   671→                    let cellStyle = {
   672→                        border: borderStyle,
   673→                        alignment: alignCenter,
   674→                        font: { name: "Calibri", sz: 11 }
   675→                    };
   676→
   677→                    if (R === 0) {
   678→                        // 表头样式
   679→                        cellStyle.font.bold = true;
   680→                        cellStyle.font.color = { rgb: colorWhite };
   681→
   682→                        let bgColor = colorBlue;
   683→                        if (C === 3) bgColor = colorGreen;
   684→                        else if (C === 4) bgColor = colorRed;
   685→                        else if (C === 5) bgColor = colorYellow;
   686→                        else if (C === 6) bgColor = colorPurple;
   687→
   688→                        cellStyle.fill = { fgColor: { rgb: bgColor } };
   689→                    } else {
   690→                        const dataRowIndex = R - 1;
   691→
   692→                        if (C === 0) {
   693→                            cellStyle.font.bold = true;
   694→                        } else if (C >= 2 && C <= 6 && dataRowIndex < changeInfo.length) {
   695→                            const changes = changeInfo[dataRowIndex];
   696→                            let changeValue = null;
   697→
   698→                            if (C === 2) changeValue = changes.dur;
   699→                            else if (C === 3) changeValue = changes.lev;
   700→                            else if (C === 4) changeValue = changes.eq;
   701→                            else if (C === 5) changeValue = changes.der;
