   703→
   704→                            if (changeValue !== null && !isNaN(changeValue) && Math.abs(changeValue) >= 0.000001) {
   705→                                const isPositive = changeValue > 0;
   706→                                // 增加用红色，减少用绿色
   707→                                cellStyle.font.color = { rgb: isPositive ? colorRed : colorGreen };
   708→                                cellStyle.font.bold = true;
