function exportToExcel() {
    const currentDate = window.currentDateStr || '当期';

    const wb = XLSX.utils.book_new();

    // 创建工作表数据 - 只包含主数值
    const wsData = [
        [
            "产品系列",
            "规模(亿)",
            `${currentDate}久期`,
            `${currentDate}杠杆`,
            `${currentDate}权益仓位`,
            `${currentDate}衍生品仓位`,
            "20251231黄金及商品仓位"
        ]
    ];

    const changeInfo = [];

    generatedData.forEach(row => {
        changeInfo.push({
            dur: row.durChg,
            lev: row.levChg,
            eq: row.eqChg,
            der: row.derChg
        });

        wsData.push([
            row.series,
            formatValue(row.scale, 'integer'),
            formatValue(row.dur, 'decimal'),
            formatValue(row.lev, 'percent_int'),
            formatValue(row.eq, 'percent'),
            formatValue(row.der, 'percent'),
            row.series === '稳享' ? formatValue(commodityData.current, 'percent') : ''
        ]);
    });

    const ws = XLSX.utils.aoa_to_sheet(wsData);

    // 合并黄金商品仓位单元格
    if (!ws['!merges']) ws['!merges'] = [];
    ws['!merges'].push({
        s: { r: 1, c: 6 },
        e: { r: MAPPING.length, c: 6 }
    });

    const borderStyle = {
        top: { style: "thin" },
        bottom: { style: "thin" },
        left: { style: "thin" },
        right: { style: "thin" }
    };
    const alignCenter = {
        horizontal: "center",
        vertical: "center",
        wrapText: false
    };

    const colorBlue = "4472C4";
    const colorGreen = "548235";
    const colorRed = "C00000";
    const colorYellow = "FFC000";
    const colorPurple = "7030A0";
    const colorWhite = "FFFFFF";
    const colorBlack = "000000";

    const range = XLSX.utils.decode_range(ws['!ref']);

    // 应用样式并添加富文本
    for (let R = range.s.r; R <= range.e.r; ++R) {
        for (let C = range.s.c; C <= range.e.c; ++C) {
            const cell_address = XLSX.utils.encode_cell({ r: R, c: C });
            if (!ws[cell_address]) continue;

            let cellStyle = {
                border: borderStyle,
                alignment: alignCenter,
                font: { name: "Calibri", sz: 11 }
            };

            if (R === 0) {
                // 表头样式
                cellStyle.font.bold = true;
                cellStyle.font.color = { rgb: colorWhite };

                let bgColor = colorBlue;
                if (C === 3) bgColor = colorGreen;
                else if (C === 4) bgColor = colorRed;
                else if (C === 5) bgColor = colorYellow;
                else if (C === 6) bgColor = colorPurple;

                cellStyle.fill = { fgColor: { rgb: bgColor } };
            } else {
                const dataRowIndex = R - 1;

                if (C === 0) {
                    cellStyle.font.bold = true;
                } else if (C >= 2 && C <= 6 && dataRowIndex < changeInfo.length) {
                    // 处理有变化的单元格，使用富文本
                    const changes = changeInfo[dataRowIndex];
                    let changeValue = null;
                    let mainValue = ws[cell_address].v;

                    if (C === 2) changeValue = changes.dur;
                    else if (C === 3) changeValue = changes.lev;
                    else if (C === 4) changeValue = changes.eq;
                    else if (C === 5) changeValue = changes.der;
                    else if (C === 6) changeValue = commodityData.change;

                    if (changeValue !== null && !isNaN(changeValue) && Math.abs(changeValue) >= 0.000001) {
                        const isPositive = changeValue > 0;
                        const arrow = isPositive ? '↑' : '↓';
                        const sign = isPositive ? '+' : '';
                        const changeColor = isPositive ? colorRed : colorGreen;

                        let changeText = '';
                        if (C === 2) changeText = formatValue(Math.abs(changeValue), 'decimal');
                        else if (C === 3) changeText = formatValue(Math.abs(changeValue), 'percent_int');
                        else changeText = formatValue(Math.abs(changeValue), 'percent');

                        // 使用富文本格式
                        ws[cell_address].t = 's'; // 设置为字符串类型
                        ws[cell_address].v = mainValue + ' ' + arrow + sign + changeText;
                        ws[cell_address].s = cellStyle;

                        // 添加富文本（注意：xlsx-js-style可能不完全支持）
                        ws[cell_address].r = [
                            {
                                t: mainValue + ' ',
                                s: {
                                    font: { name: "Calibri", sz: 11, color: { rgb: colorBlack } }
                                }
                            },
                            {
                                t: arrow + sign + changeText,
                                s: {
                                    font: {
                                        name: "Calibri",
                                        sz: 8,
                                        color: { rgb: changeColor },
                                        vertAlign: 'superscript'
                                    }
                                }
                            }
                        ];
                    }
                }
            }

            ws[cell_address].s = cellStyle;
        }
    }

    ws['!cols'] = [
        { wch: 12 },
        { wch: 10 },
        { wch: 15 },
        { wch: 13 },
        { wch: 16 },
        { wch: 18 },
        { wch: 20 }
    ];

    ws['!rows'] = [{ hpt: 30 }];
    for (let i = 1; i <= generatedData.length; i++) {
        ws['!rows'][i] = { hpt: 35 };
    }

    XLSX.utils.book_append_sheet(wb, ws, "产品运作概览");

    const date = new Date();
    const dateStr = date.toISOString().split('T')[0];
    XLSX.writeFile(wb, `产品运作概览信息表_${dateStr}.xlsx`);
}

