function exportToExcel() {
    const currentDate = window.currentDateStr || '当期';

    const wb = XLSX.utils.book_new();

    // 创建工作表数据 - 主值在第一行，变化值在第二行
    const wsData = [
        [
            "产品系列",
            "规模(亿)",
            `${currentDate}久期`,
            `${currentDate}杠杆`,
            `${currentDate}权益仓位`,
            `${currentDate}衍生品仓位`,
            "20251231黄金及商品仓位"
        ]
    ];

    const cellFormats = []; // 存储需要特殊格式的单元格信息

    generatedData.forEach((row, rowIndex) => {
        const rowFormats = [];

        // 构建每个单元格的显示值和变化信息
        const durText = formatValue(row.dur, 'decimal');
        const durChange = row.durChg;
        const levText = formatValue(row.lev, 'percent_int');
        const levChange = row.levChg;
        const eqText = formatValue(row.eq, 'percent');
        const eqChange = row.eqChg;
        const derText = formatValue(row.der, 'percent');
        const derChange = row.derChg;

        // 生成带换行的单元格值
        function getCellValue(mainValue, change, type) {
            if (mainValue === '-') return '-';
            if (change === null || change === undefined || isNaN(change) || Math.abs(change) < 0.000001) {
                return mainValue;
            }
            const isPositive = change > 0;
            const arrow = isPositive ? '↑' : '↓';
            const sign = isPositive ? '+' : '';
            const changeText = formatValue(Math.abs(change), type);
            return mainValue + '\n' + arrow + sign + changeText;
        }

        rowFormats.push(
            null, // 产品系列
            null, // 规模
            { change: durChange },
            { change: levChange },
            { change: eqChange },
            { change: derChange },
            rowIndex === 0 ? { change: commodityData.change } : null
        );

        cellFormats.push(rowFormats);

        wsData.push([
            row.series,
            formatValue(row.scale, 'integer'),
            getCellValue(durText, durChange, 'decimal'),
            getCellValue(levText, levChange, 'percent_int'),
            getCellValue(eqText, eqChange, 'percent'),
            getCellValue(derText, derChange, 'percent'),
            row.series === '稳享' ? getCellValue(formatValue(commodityData.current, 'percent'), commodityData.change, 'percent') : ''
        ]);
    });

    const ws = XLSX.utils.aoa_to_sheet(wsData);

    // 合并黄金商品仓位单元格
    if (!ws['!merges']) ws['!merges'] = [];
    ws['!merges'].push({
        s: { r: 1, c: 6 },
        e: { r: MAPPING.length, c: 6 }
    });

    const borderStyle = {
        top: { style: "thin" },
        bottom: { style: "thin" },
        left: { style: "thin" },
        right: { style: "thin" }
    };
    const alignCenter = {
        horizontal: "center",
        vertical: "center",
        wrapText: true  // 启用自动换行
    };

    const colorBlue = "4472C4";
    const colorGreen = "548235";
    const colorRed = "C00000";
    const colorYellow = "FFC000";
    const colorPurple = "7030A0";
    const colorWhite = "FFFFFF";
    const colorBlack = "000000";

    const range = XLSX.utils.decode_range(ws['!ref']);

    // 应用样式和富文本
    for (let R = range.s.r; R <= range.e.r; ++R) {
        for (let C = range.s.c; C <= range.e.c; ++C) {
            const cell_address = XLSX.utils.encode_cell({ r: R, c: C });
            if (!ws[cell_address]) continue;

            let cellStyle = {
                border: borderStyle,
                alignment: alignCenter,
                font: { name: "Calibri", sz: 11 }
            };

            if (R === 0) {
                // 表头样式
                cellStyle.font.bold = true;
                cellStyle.font.color = { rgb: colorWhite };

                let bgColor = colorBlue;
                if (C === 3) bgColor = colorGreen;
                else if (C === 4) bgColor = colorRed;
                else if (C === 5) bgColor = colorYellow;
                else if (C === 6) bgColor = colorPurple;

                cellStyle.fill = { fgColor: { rgb: bgColor } };
            } else {
                const dataRowIndex = R - 1;

                if (C === 0) {
                    cellStyle.font.bold = true;
                } else if (C >= 2 && C <= 6 && dataRowIndex < cellFormats.length) {
                    const formatInfo = cellFormats[dataRowIndex][C];

                    if (formatInfo && formatInfo.change !== null && !isNaN(formatInfo.change) && Math.abs(formatInfo.change) >= 0.000001) {
                        const cellValue = ws[cell_address].v;
                        const parts = cellValue.split('\n');

                        if (parts.length === 2) {
                            const mainValue = parts[0];
                            const changeValue = parts[1];
                            const isPositive = formatInfo.change > 0;
                            const changeColor = isPositive ? colorRed : colorGreen;

                            // 使用富文本格式
                            ws[cell_address].t = 's';
                            delete ws[cell_address].v;
                            delete ws[cell_address].w;

                            ws[cell_address].r = [
                                {
                                    t: mainValue + '\n',
                                    s: {
                                        font: {
                                            name: "Calibri",
                                            sz: 11,
                                            color: { rgb: colorBlack }
                                        }
                                    }
                                },
                                {
                                    t: changeValue,
                                    s: {
                                        font: {
                                            name: "Calibri",
                                            sz: 11,
                                            color: { rgb: changeColor },
                                            bold: true
                                        }
                                    }
                                }
                            ];
                        }
                    }
                }
            }

            ws[cell_address].s = cellStyle;
        }
    }

    ws['!cols'] = [
        { wch: 12 },
        { wch: 10 },
        { wch: 16 },
        { wch: 14 },
        { wch: 18 },
        { wch: 18 },
        { wch: 22 }
    ];

    ws['!rows'] = [{ hpt: 30 }];
    for (let i = 1; i <= generatedData.length; i++) {
        ws['!rows'][i] = { hpt: 45 };  // 增加行高以容纳两行内容
    }

    XLSX.utils.book_append_sheet(wb, ws, "产品运作概览");

    const date = new Date();
    const dateStr = date.toISOString().split('T')[0];
    XLSX.writeFile(wb, `产品运作概览信息表_${dateStr}.xlsx`);
}

