        const levChange = row.levChg;
        const eqText = formatValue(row.eq, 'percent');
        const eqChange = row.eqChg;
        const derText = formatValue(row.der, 'percent');
        const derChange = row.derChg;

        rowFormats.push(
            null, // 产品系列
            null, // 规模
            { value: durText, change: durChange, type: 'decimal' },
            { value: levText, change: levChange, type: 'percent_int' },
            { value: eqText, change: eqChange, type: 'percent' },
            { value: derText, change: derChange, type: 'percent' },
            rowIndex === 0 ? { value: formatValue(commodityData.current, 'percent'), change: commodityData.change, type: 'percent' } : null
        );

        cellFormats.push(rowFormats);

        wsData.push([
            row.series,
            formatValue(row.scale, 'integer'),
            durText,
            levText,
            eqText,
            derText,
            row.series === '稳享' ? formatValue(commodityData.current, 'percent') : ''
        ]);
    });

    const ws = XLSX.utils.aoa_to_sheet(wsData);

    // 合并黄金商品仓位单元格
    if (!ws['!merges']) ws['!merges'] = [];
    ws['!merges'].push({
        s: { r: 1, c: 6 },
        e: { r: MAPPING.length, c: 6 }
    });

    const borderStyle = {
        top: { style: "thin" },
        bottom: { style: "thin" },
        left: { style: "thin" },
        right: { style: "thin" }
    };
    const alignCenter = {
        horizontal: "center",
        vertical: "center",
        wrapText: false
    };

    const colorBlue = "4472C4";
    const colorGreen = "548235";
    const colorRed = "C00000";
    const colorYellow = "FFC000";
    const colorPurple = "7030A0";
    const colorWhite = "FFFFFF";

    const range = XLSX.utils.decode_range(ws['!ref']);

    // 应用样式
    for (let R = range.s.r; R <= range.e.r; ++R) {
        for (let C = range.s.c; C <= range.e.c; ++C) {
            const cell_address = XLSX.utils.encode_cell({ r: R, c: C });
            if (!ws[cell_address]) continue;

            let cellStyle = {
                border: borderStyle,
                alignment: alignCenter,
                font: { name: "Calibri", sz: 11 }
            };

            if (R === 0) {
                // 表头样式
                cellStyle.font.bold = true;
                cellStyle.font.color = { rgb: colorWhite };

                let bgColor = colorBlue;
                if (C === 3) bgColor = colorGreen;
                else if (C === 4) bgColor = colorRed;
                else if (C === 5) bgColor = colorYellow;
                else if (C === 6) bgColor = colorPurple;

                cellStyle.fill = { fgColor: { rgb: bgColor } };
            } else {
                const dataRowIndex = R - 1;

                if (C === 0) {
                    cellStyle.font.bold = true;
                } else if (C >= 2 && C <= 6 && dataRowIndex < cellFormats.length) {
                    const formatInfo = cellFormats[dataRowIndex][C];

                    if (formatInfo && formatInfo.change !== null && !isNaN(formatInfo.change) && Math.abs(formatInfo.change) >= 0.000001) {
                        const isPositive = formatInfo.change > 0;
                        const arrow = isPositive ? '↑' : '↓';
                        const sign = isPositive ? '+' : '';

                        let changeText = '';
                        if (formatInfo.type === 'decimal') changeText = formatValue(Math.abs(formatInfo.change), 'decimal');
                        else if (formatInfo.type === 'percent_int') changeText = formatValue(Math.abs(formatInfo.change), 'percent_int');
                        else changeText = formatValue(Math.abs(formatInfo.change), 'percent');

                        // 添加批注显示变化信息
                        const changeInfo = `${arrow}${sign}${changeText}`;
                        ws[cell_address].c = [{
                            a: "变化",
                            t: changeInfo,
                            s: { color: { rgb: isPositive ? colorRed : colorGreen } }
                        }];

                        // 修改单元格值，添加变化信息
                        ws[cell_address].v = formatInfo.value + ' ' + changeInfo;

                        // 尝试使用富文本
                        ws[cell_address].s = cellStyle;
                    }
                }
            }

            ws[cell_address].s = cellStyle;
        }
    }

    ws['!cols'] = [
        { wch: 12 },
        { wch: 10 },
        { wch: 16 },
        { wch: 16 },
        { wch: 18 },
        { wch: 18 },
        { wch: 22 }
    ];

    ws['!rows'] = [{ hpt: 30 }];
    for (let i = 1; i <= generatedData.length; i++) {
        ws['!rows'][i] = { hpt: 35 };
    }

    XLSX.utils.book_append_sheet(wb, ws, "产品运作概览");

    const date = new Date();
    const dateStr = date.toISOString().split('T')[0];
    XLSX.writeFile(wb, `产品运作概览信息表_${dateStr}.xlsx`);
}

