python3 << 'EOF'
import json

with open('/Users/fanshengxia/Desktop/市场观点美化/asset-analysis-real-data/asset-analysis-dashboard/src/assets/asset_data.json', 'r') as f:
    data = json.load(f)

# 遍历所有类别和资产查找纯债溢价率和隐含波动率
for category_key in ['equity', 'bond', 'forex', 'commodity']:
    category = data.get(category_key, {})
    for asset_name, asset_data in category.items():
        if isinstance(asset_data, dict) and 'indicators' in asset_data:
            for ind in asset_data['indicators']:
                if '纯债溢价率' in ind.get('indicator', '') or '隐含波动率' in ind.get('indicator', ''):
                    print(f"类别: {category_key}, 资产: {asset_name}")
                    print(f"  {ind}")

# 也看看有没有中证转债
print("\n所有债券资产:")
for asset_name in data.get('bond', {}).keys():
    print(f"  {asset_name}")
EOF
