# -*- coding: utf-8 -*-
"""
数据验证模块 - 确保投资数据计算的准确性和完整性
专为投资经理设计，严格验证金融数据质量
"""
import pandas as pd
import numpy as np
import os
from typing import Tuple, List, Dict, Any
import warnings

class DataValidator:
    """数据验证类 - 对投资数据进行严格验证"""

    def __init__(self, log_errors=True):
        self.log_errors = log_errors
        self.validation_errors = []
        self.validation_warnings = []

    def validate_file_exists(self, file_path: str, file_description: str = "") -> bool:
        """验证文件是否存在"""
        if not os.path.exists(file_path):
            error_msg = f"关键数据文件缺失: {file_description} - {file_path}"
            self.validation_errors.append(error_msg)
            if self.log_errors:
                print(f"❌ 错误: {error_msg}")
            return False
        else:
            if self.log_errors:
                print(f"✅ 文件存在: {file_description}")
            return True

    def validate_dataframe_not_empty(self, df: pd.DataFrame, data_name: str) -> bool:
        """验证数据框不为空"""
        if df is None or df.empty:
            error_msg = f"数据为空: {data_name}"
            self.validation_errors.append(error_msg)
            if self.log_errors:
                print(f"❌ 错误: {error_msg}")
            return False
        else:
            if self.log_errors:
                print(f"✅ 数据正常: {data_name} ({len(df)} 行)")
            return True

    def validate_required_columns(self, df: pd.DataFrame, required_columns: List[str], data_name: str) -> bool:
        """验证必需的列是否存在"""
        missing_columns = [col for col in required_columns if col not in df.columns]
        if missing_columns:
            error_msg = f"{data_name} 缺少必需列: {missing_columns}"
            self.validation_errors.append(error_msg)
            if self.log_errors:
                print(f"❌ 错误: {error_msg}")
            return False
        else:
            if self.log_errors:
                print(f"✅ 列验证通过: {data_name}")
            return True

    def validate_numeric_columns(self, df: pd.DataFrame, numeric_columns: List[str], data_name: str) -> bool:
        """验证数值列的数据类型和范围"""
        all_valid = True
        for col in numeric_columns:
            if col in df.columns:
                # 检查是否包含非数值数据
                non_numeric = df[col].apply(lambda x: not pd.isna(x) and not isinstance(x, (int, float, np.number)))
                if non_numeric.any():
                    warning_msg = f"{data_name} 列 {col} 包含非数值数据"
                    self.validation_warnings.append(warning_msg)
                    if self.log_errors:
                        print(f"⚠️  警告: {warning_msg}")

                # 检查异常值（例如负的净值、过大的收益率等）
                if col in ['单位净值', '累计净值']:
                    invalid_values = df[df[col] <= 0][col] if not df[col].empty else pd.Series()
                    if not invalid_values.empty:
                        error_msg = f"{data_name} 列 {col} 包含非正值: {len(invalid_values)} 个"
                        self.validation_errors.append(error_msg)
                        all_valid = False
                        if self.log_errors:
                            print(f"❌ 错误: {error_msg}")

                elif '收益率' in col or '年化' in col:
                    # 检查收益率是否在合理范围内（-100% 到 1000%）
                    extreme_values = df[(df[col] < -1) | (df[col] > 10)][col] if not df[col].empty else pd.Series()
                    if not extreme_values.empty:
                        warning_msg = f"{data_name} 列 {col} 包含极值: {len(extreme_values)} 个"
                        self.validation_warnings.append(warning_msg)
                        if self.log_errors:
                            print(f"⚠️  警告: {warning_msg}")

        return all_valid

    def validate_date_columns(self, df: pd.DataFrame, date_columns: List[str], data_name: str) -> bool:
        """验证日期列的格式和逻辑"""
        all_valid = True
        for col in date_columns:
            if col in df.columns:
                try:
                    # 尝试转换为日期格式
                    date_series = pd.to_datetime(df[col], errors='coerce')
                    invalid_dates = date_series.isna().sum()
                    if invalid_dates > 0:
                        error_msg = f"{data_name} 列 {col} 包含 {invalid_dates} 个无效日期"
                        self.validation_errors.append(error_msg)
                        all_valid = False
                        if self.log_errors:
                            print(f"❌ 错误: {error_msg}")
                except Exception as e:
                    error_msg = f"{data_name} 列 {col} 日期格式错误: {str(e)}"
                    self.validation_errors.append(error_msg)
                    all_valid = False
                    if self.log_errors:
                        print(f"❌ 错误: {error_msg}")

        return all_valid

    def validate_net_value_series(self, df: pd.DataFrame, product_code: str) -> bool:
        """验证净值序列的逻辑一致性"""
        all_valid = True

        if '单位净值' in df.columns:
            net_values = df['单位净值'].dropna()
            if not net_values.empty:
                # 检查净值是否单调递增（允许小幅波动）
                decreases = net_values.diff() < -0.1  # 大于10%的下跌可能有问题
                large_decreases = decreases.sum()
                if large_decreases > len(net_values) * 0.1:  # 超过10%的数据点大幅下跌
                    warning_msg = f"产品 {product_code} 净值序列存在异常下跌: {large_decreases} 个点"
                    self.validation_warnings.append(warning_msg)
                    if self.log_errors:
                        print(f"⚠️  警告: {warning_msg}")

                # 检查净值跳跃（单日涨跌超过50%）
                large_changes = abs(net_values.pct_change()) > 0.5
                extreme_changes = large_changes.sum()
                if extreme_changes > 0:
                    warning_msg = f"产品 {product_code} 净值存在异常跳跃: {extreme_changes} 个点"
                    self.validation_warnings.append(warning_msg)
                    if self.log_errors:
                        print(f"⚠️  警告: {warning_msg}")

        return all_valid

    def validate_calculation_results(self, results: Dict[str, Any], expected_ranges: Dict[str, Tuple[float, float]] = None) -> bool:
        """验证计算结果的合理性"""
        all_valid = True

        if expected_ranges is None:
            expected_ranges = {
                '年化收益率': (-0.5, 1.0),    # -50% 到 100%
                '年化波动率': (0, 1.0),       # 0% 到 100%
                '最大回撤': (-1.0, 0),        # -100% 到 0%
                '夏普比率': (-5.0, 5.0),      # -5 到 5
            }

        for key, value in results.items():
            if pd.isna(value):
                continue

            # 检查是否在预期范围内
            for metric_name, (min_val, max_val) in expected_ranges.items():
                if metric_name in key:
                    if not (min_val <= value <= max_val):
                        warning_msg = f"计算结果异常: {key} = {value:.4f} (预期范围: {min_val} - {max_val})"
                        self.validation_warnings.append(warning_msg)
                        if self.log_errors:
                            print(f"⚠️  警告: {warning_msg}")
                    break

        return all_valid

    def get_validation_summary(self) -> Dict[str, Any]:
        """获取验证结果摘要"""
        return {
            'errors_count': len(self.validation_errors),
            'warnings_count': len(self.validation_warnings),
            'errors': self.validation_errors,
            'warnings': self.validation_warnings,
            'is_valid': len(self.validation_errors) == 0
        }

    def print_validation_summary(self):
        """打印验证结果摘要"""
        summary = self.get_validation_summary()

        print("\n" + "="*60)
        print("📊 数据验证结果摘要")
        print("="*60)

        if summary['is_valid']:
            print("✅ 数据验证通过！所有关键验证项目正常。")
        else:
            print(f"❌ 发现 {summary['errors_count']} 个错误需要修复")

        if summary['warnings_count'] > 0:
            print(f"⚠️  发现 {summary['warnings_count']} 个警告项目")

        if summary['errors']:
            print("\n🔴 错误详情:")
            for i, error in enumerate(summary['errors'], 1):
                print(f"  {i}. {error}")

        if summary['warnings']:
            print("\n🟡 警告详情:")
            for i, warning in enumerate(summary['warnings'], 1):
                print(f"  {i}. {warning}")

        print("="*60)

def safe_read_excel(file_path: str, **kwargs) -> pd.DataFrame:
    """安全读取Excel文件，包含错误处理"""
    validator = DataValidator()

    if not validator.validate_file_exists(file_path, "Excel文件"):
        raise FileNotFoundError(f"文件不存在: {file_path}")

    try:
        df = pd.read_excel(file_path, **kwargs)
        validator.validate_dataframe_not_empty(df, os.path.basename(file_path))
        return df
    except Exception as e:
        raise Exception(f"读取Excel文件失败: {file_path}\n错误: {str(e)}")

def validate_critical_paths(config_class):
    """验证关键路径是否存在"""
    validator = DataValidator()

    # 验证数据目录
    validator.validate_file_exists(config_class.DATA_DIR, "数据目录")
    validator.validate_file_exists(config_class.OUTPUT_DIR, "输出目录")

    # 验证子目录
    validator.validate_file_exists(config_class.NET_VALUE_DIR, "产品净值数据目录")
    validator.validate_file_exists(config_class.WEEKLY_DATA_DIR, "周度更新数据目录")
    validator.validate_file_exists(config_class.ASSET_DIR, "资产持仓数据目录")

    return validator.get_validation_summary()

if __name__ == "__main__":
    print("数据验证模块已加载")
    print("主要功能:")
    print("- 文件存在性验证")
    print("- 数据完整性验证") 
    print("- 数值范围验证")
    print("- 日期格式验证")
    print("- 净值序列逻辑验证")
    print("- 计算结果合理性验证")

