        return False
    if isinstance(val, (int, float)):
        return not (isinstance(val, float) and (math.isnan(val) or math.isinf(val)))
    if isinstance(val, str):
        try:
            float(val)
            return True
        except:
            return False
    return False


def to_float(val):
    if val is None:
        return None
    if isinstance(val, (int, float)):
        if isinstance(val, float) and (math.isnan(val) or math.isinf(val)):
            return None
        return float(val)
    if isinstance(val, str):
        if val in ('nan', 'inf', '-inf', ''):
            return None
        try:
            return float(val)
        except:
            return None
    return None


