def main():
    ctx = init_context()
    writer = pd.ExcelWriter(Config.get_current_output_writer(ctx.date))

    print("\n" + "=" * 60)
    print("📈 生成报表...")
    print("=" * 60)

    results = {}
    steps = [
        ("渠道分析", generate_channel_report),
        ("产品规模", generate_scale_report),
        ("规模变化与新发产品", generate_scale_change_report),
        ("产品业绩", generate_performance_report),
        ("投资经理分析", generate_manager_report),
    ]
    for name, func in steps:
        print(f"\n📈 {name}...")
        try:
            ret = func(ctx, writer)
            if ret:
                results.update(ret)
        except Exception as e:
            print(f"❌ {name} 失败: {e}")
            raise

    # 招商产品分析需要Channel_df
    print(f"\n📈 招商产品分析...")
    try:
        ret = generate_zhaoshang_report(ctx, writer, Channel_df=results.get('Channel_df'))
        if ret:
            results.update(ret)
    except Exception as e:
        print(f"❌ 招商产品分析 失败: {e}")
        raise

    # 资产大表
    print(f"\n📈 资产大表...")
    try:
        ret = generate_asset_table(ctx, writer)
        if ret:
            results.update(ret)
    except Exception as e:
        print(f"❌ 资产大表 失败: {e}")
        raise

    # 中收监控
    print(f"\n📈 中收监控...")
    try:
        ret = generate_fee_report(ctx, writer)
        if ret:
            results.update(ret)
    except Exception as e:
        print(f"❌ 中收监控 失败: {e}")
        raise

    # 产品到期
    print(f"\n📈 产品到期...")
    try:
        ret = generate_maturity_report(ctx, writer)
        if ret:
            results.update(ret)
    except Exception as e:
        print(f"❌ 产品到期 失败: {e}")
        raise

    # 持仓分析需要Chicang_df
    print(f"\n📈 持仓分析...")
    try:
        ret = generate_holdings_report(ctx, writer, Chicang_df=results.get('Chicang_df'))
        if ret:
            results.update(ret)
    except Exception as e:
        print(f"❌ 持仓分析 失败: {e}")
        raise

    # 剩余步骤
    remaining_steps = [
        ("净值波动", generate_volatility_report),
        ("周期分布", generate_cycle_report),
        ("市场指数收益", generate_market_index_report),
        ("破净结果", generate_pojing_report),
    ]
    for name, func in remaining_steps:
        print(f"\n📈 {name}...")
        try:
            ret = func(ctx, writer)
            if ret:
                results.update(ret)
        except Exception as e:
            print(f"❌ {name} 失败: {e}")
            raise

    # 总结文字
    generate_summary_text(ctx, writer, results)

    writer.close()

    print("\n" + "=" * 60)
    print("✅ 周报结果表生成完成！")
    print(f"   输出文件: {Config.get_current_output_writer(ctx.date)}")
    print("=" * 60)


if __name__ == '__main__':
    main()

