      if (curGroup) { if (!groups[curGroup]) groups[curGroup] = []; groups[curGroup].push(row); }
    }
    const grandTotal = [0, 0];
    for (const gn of ['中低波稳健产品', '中高波市值产品', '外币产品']) {
