class EditorManager {
    constructor() {
        this.quill = null;
        this.currentPageId = null;
        this.autoSaveTimeout = null;
        this.saveDelay = 500; // è‡ªåŠ¨ä¿å­˜å»¶è¿Ÿï¼ˆæ¯«ç§’ï¼‰
    }

    // åˆå§‹åŒ–ç¼–è¾‘å™¨
    init() {
        const toolbarOptions = [
            [{ 'header': [1, 2, 3, 4, false] }],
            ['bold', 'italic', 'underline', 'strike'],
            [{ 'color': [] }, { 'background': [] }],
            [{ 'list': 'ordered'}, { 'list': 'bullet' }, { 'list': 'check' }],
            [{ 'indent': '-1'}, { 'indent': '+1' }],
            [{ 'align': [] }],
            ['link', 'image'],
            ['blockquote', 'code-block'],
            ['clean']
        ];

        this.quill = new Quill('#editor-container', {
            theme: 'snow',
            modules: {
                toolbar: toolbarOptions
            },
            placeholder: 'å¼€å§‹å†™ä½œ...'
        });

        // ç›‘å¬å†…å®¹å˜åŒ–
        this.quill.on('text-change', () => {
            this.onContentChange();
        });

        // ç›‘å¬ç²˜è´´äº‹ä»¶ï¼ˆç”¨äºå›¾ç‰‡ç²˜è´´ï¼‰
        this.quill.root.addEventListener('paste', (e) => {
            this.handlePaste(e);
        });

        // è‡ªå®šä¹‰å·¥å…·æ æŒ‰é’®
        this.initCustomButtons();

        console.log('Editor initialized');
    }

    // åˆå§‹åŒ–è‡ªå®šä¹‰å·¥å…·æ æŒ‰é’®
    initCustomButtons() {
        // æ’å…¥è¡¨æ ¼æŒ‰é’®
        const tableBtn = document.getElementById('btn-insert-table');
        if (tableBtn) {
            tableBtn.addEventListener('click', () => {
                this.insertTable();
            });
        }

        // æ’å…¥äº¤æ˜“æ¨¡æ¿æŒ‰é’®
        const tradeTemplateBtn = document.getElementById('btn-insert-trade-template');
        if (tradeTemplateBtn) {
            tradeTemplateBtn.addEventListener('click', () => {
                this.insertTradeTemplate();
            });
        }
    }

    // åŠ è½½é¡µé¢å†…å®¹
    async loadPage(pageId) {
        if (!pageId) {
            this.clear();
            return;
        }

        try {
            const page = await db.get('pages', pageId);
            if (!page) {
                console.error('Page not found:', pageId);
                return;
            }

            this.currentPageId = pageId;

            // è®¾ç½®æ ‡é¢˜
            const titleInput = document.getElementById('page-title');
            titleInput.value = page.title || '';

            // è®¾ç½®å†…å®¹
            if (page.content && page.content.ops) {
                this.quill.setContents(page.content);
            } else {
                this.quill.setText('');
            }

            // æ›´æ–°å…ƒæ•°æ®æ˜¾ç¤º
            this.updateMetadata(page);

            // æ›´æ–°å­—æ•°ç»Ÿè®¡
