            if (this.currentPageId) {
                await db.addAttachment(
                    this.currentPageId,
                    file.name || 'pasted-image.png',
                    file.type,
                    file.size,
                    base64
                );
                await this.loadAttachments(this.currentPageId);
            }

            showNotification('å›¾ç‰‡å·²æ’å…¥', 'success');
        } catch (error) {
            console.error('Error inserting image:', error);
            showNotification('æ’å…¥å›¾ç‰‡å¤±è´¥', 'error');
        }
    }

    // æ’å…¥è¡¨æ ¼
    insertTable() {
        const rows = promptDialog('è¯·è¾“å…¥è¡Œæ•°:', '3');
        const cols = promptDialog('è¯·è¾“å…¥åˆ—æ•°:', '3');

        if (!rows || !cols) return;

        const rowCount = parseInt(rows);
        const colCount = parseInt(cols);

        if (isNaN(rowCount) || isNaN(colCount) || rowCount < 1 || colCount < 1) {
            showNotification('è¯·è¾“å…¥æœ‰æ•ˆçš„è¡Œåˆ—æ•°', 'error');
            return;
        }

        // ç”Ÿæˆè¡¨æ ¼HTMLï¼ˆç®€åŒ–ç‰ˆï¼‰
        let tableHtml = '<table border="1" style="border-collapse: collapse; width: 100%;">\n';
        for (let i = 0; i < rowCount; i++) {
            tableHtml += '  <tr>\n';
            for (let j = 0; j < colCount; j++) {
                tableHtml += '    <td style="padding: 8px; border: 1px solid #ddd;">&nbsp;</td>\n';
            }
            tableHtml += '  </tr>\n';
        }
        tableHtml += '</table>';

        // æ’å…¥è¡¨æ ¼
        const range = this.quill.getSelection();
        const index = range ? range.index : this.quill.getLength();
        this.quill.clipboard.dangerouslyPasteHTML(index, tableHtml);

        showNotification('è¡¨æ ¼å·²æ’å…¥', 'success');
    }

    // æ’å…¥äº¤æ˜“æ¨¡æ¿å¿«é€Ÿè¡¨å•
    insertTradeTemplate() {
        const range = this.quill.getSelection();
        const index = range ? range.index : this.quill.getLength();

        // æ’å…¥é¢„è®¾çš„äº¤æ˜“ä¿¡æ¯æ¨¡æ¿
        const templateText = `
äº¤æ˜“ä¿¡æ¯
å“ç§ä»£ç :
äº¤æ˜“æ–¹å‘:
æˆäº¤ä»·æ ¼:
æˆäº¤æ•°é‡:

å¸‚åœºåˆ†æ


äº¤æ˜“é€»è¾‘

`;

        this.quill.insertText(index, templateText);
        showNotification('äº¤æ˜“æ¨¡æ¿å·²æ’å…¥', 'success');
    }

    // åŠ è½½é™„ä»¶åˆ—è¡¨
    async loadAttachments(pageId) {
        const container = document.getElementById('attachments-list');
        container.innerHTML = '';

        try {
            const attachments = await db.getAttachmentsByPage(pageId);

            if (attachments.length === 0) {
                container.innerHTML = '<p style="color: #9ca3af; font-size: 13px; text-align: center;">æš‚æ— é™„ä»¶</p>';
                return;
            }

            attachments.forEach(attachment => {
                const item = document.createElement('div');
                item.className = 'attachment-item';

                const icon = document.createElement('span');
                icon.className = 'attachment-icon';
                icon.textContent = getFileIcon(attachment.name);

                const info = document.createElement('div');
                info.className = 'attachment-info';

                const name = document.createElement('div');
                name.className = 'attachment-name';
                name.textContent = attachment.name;

                const size = document.createElement('div');
                size.className = 'attachment-size';
                size.textContent = formatFileSize(attachment.size);

                info.appendChild(name);
                info.appendChild(size);

                item.appendChild(icon);
                item.appendChild(info);

                // ç‚¹å‡»ä¸‹è½½
                item.addEventListener('click', () => {
                    this.downloadAttachment(attachment);
                });

                container.appendChild(item);
            });
        } catch (error) {
            console.error('Error loading attachments:', error);
        }
    }

    // ä¸‹è½½é™„ä»¶
    downloadAttachment(attachment) {
        try {
            // ä»Base64åˆ›å»ºBlob
            const base64Data = attachment.data.split(',')[1];
            const byteCharacters = atob(base64Data);
            const byteNumbers = new Array(byteCharacters.length);

            for (let i = 0; i < byteCharacters.length; i++) {
                byteNumbers[i] = byteCharacters.charCodeAt(i);
            }

            const byteArray = new Uint8Array(byteNumbers);
            const blob = new Blob([byteArray], { type: attachment.type });

            // ä¸‹è½½
            const url = URL.createObjectURL(blob);
            const link = document.createElement('a');
            link.href = url;
            link.download = attachment.name;
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            URL.revokeObjectURL(url);

            showNotification('é™„ä»¶å·²ä¸‹è½½', 'success');
        } catch (error) {
            console.error('Error downloading attachment:', error);
            showNotification('ä¸‹è½½é™„ä»¶å¤±è´¥', 'error');
        }
    }

    // åŠ è½½å¾…åŠåˆ—è¡¨
    async loadTodos(pageId) {
        const container = document.getElementById('todos-list');
        container.innerHTML = '';

        try {
            const todos = await db.getTodos(pageId);

            if (todos.length === 0) {
                container.innerHTML = '<p style="color: #9ca3af; font-size: 13px; text-align: center;">æš‚æ— å¾…åŠäº‹é¡¹</p>';
                return;
            }

            todos.forEach(todo => {
                const item = this.createTodoElement(todo);
                container.appendChild(item);
            });
        } catch (error) {
            console.error('Error loading todos:', error);
        }
    }

    // åˆ›å»ºå¾…åŠå…ƒç´ 
    createTodoElement(todo) {
        const item = document.createElement('div');
        item.className = 'todo-item';
        if (todo.completed) {
            item.classList.add('completed');
        }

        const checkbox = document.createElement('input');
        checkbox.type = 'checkbox';
        checkbox.className = 'todo-checkbox';
        checkbox.checked = todo.completed;
        checkbox.addEventListener('change', async () => {
            await db.updateTodo(todo.id, { completed: checkbox.checked });
            if (checkbox.checked) {
                item.classList.add('completed');
            } else {
                item.classList.remove('completed');
            }
        });

        const content = document.createElement('div');
        content.className = 'todo-content';
        content.textContent = todo.content;

        item.appendChild(checkbox);
        item.appendChild(content);

        return item;
    }

    // è·å–å½“å‰ç¼–è¾‘å™¨å†…å®¹
    getContent() {
        return this.quill.getContents();
    }

    // è·å–çº¯æ–‡æœ¬å†…å®¹
    getText() {
        return this.quill.getText();
    }

    // è®¾ç½®å†…å®¹
    setContent(delta) {
        this.quill.setContents(delta);
    }
}

