class TemplateManager {
    constructor() {
        this.currentTemplate = null;
    }

    // æ˜¾ç¤ºæ¨¡æ¿é€‰æ‹©å¯¹è¯æ¡†
    async showTemplateDialog() {
        const modal = document.getElementById('modal-template');
        const select = document.getElementById('select-template');
        const form = document.getElementById('template-form');

        // åŠ è½½æ¨¡æ¿åˆ—è¡¨
        const templates = await db.getTemplates();

        select.innerHTML = '<option value="">-- é€‰æ‹©æ¨¡æ¿ --</option>';
        templates.forEach(template => {
            const option = document.createElement('option');
            option.value = template.id;
            option.textContent = template.name;
            select.appendChild(option);
        });

        // æ¸…ç©ºè¡¨å•
        form.innerHTML = '';
        this.currentTemplate = null;

        // æ˜¾ç¤ºæ¨¡æ€æ¡†
        modal.classList.add('active');
    }

    // æ¨¡æ¿é€‰æ‹©å˜åŒ–
    async onTemplateSelect(templateId) {
        if (!templateId) {
            document.getElementById('template-form').innerHTML = '';
            this.currentTemplate = null;
            return;
        }

        try {
            const template = await db.get('templates', templateId);
            if (!template) return;

            this.currentTemplate = template;
            this.renderTemplateForm(template);
        } catch (error) {
            console.error('Error loading template:', error);
            showNotification('åŠ è½½æ¨¡æ¿å¤±è´¥', 'error');
        }
    }

    // æ¸²æŸ“æ¨¡æ¿è¡¨å•
    renderTemplateForm(template) {
        const form = document.getElementById('template-form');
        form.innerHTML = '';

        if (!template.fields || template.fields.length === 0) {
            form.innerHTML = '<p style="color: #9ca3af;">æ­¤æ¨¡æ¿æ²¡æœ‰å®šä¹‰å­—æ®µ</p>';
            return;
        }

        template.fields.forEach(field => {
            const formGroup = document.createElement('div');
            formGroup.className = 'form-group';

            const label = document.createElement('label');
            label.textContent = field.label + (field.required ? ' *' : '');

            let input;

            switch (field.type) {
                case 'text':
                    input = document.createElement('input');
                    input.type = 'text';
                    input.className = 'form-input';
                    input.name = field.name;
                    input.required = field.required;
                    break;

                case 'number':
                    input = document.createElement('input');
                    input.type = 'number';
                    input.className = 'form-input';
                    input.name = field.name;
                    input.step = 'any';
                    input.required = field.required;
                    break;

                case 'date':
                    input = document.createElement('input');
                    input.type = 'date';
                    input.className = 'form-input';
                    input.name = field.name;
                    input.value = formatDate(Date.now());
                    input.required = field.required;
                    break;

                case 'select':
                    input = document.createElement('select');
                    input.className = 'form-input';
                    input.name = field.name;
                    input.required = field.required;

                    const defaultOption = document.createElement('option');
                    defaultOption.value = '';
                    defaultOption.textContent = '-- è¯·é€‰æ‹© --';
                    input.appendChild(defaultOption);

                    if (field.options) {
                        field.options.forEach(opt => {
                            const option = document.createElement('option');
                            option.value = opt;
                            option.textContent = opt;
                            input.appendChild(option);
                        });
                    }
                    break;

                case 'richtext':
                    input = document.createElement('textarea');
                    input.className = 'form-input';
                    input.name = field.name;
                    input.rows = 4;
                    input.required = field.required;
                    break;

                default:
                    input = document.createElement('input');
                    input.type = 'text';
                    input.className = 'form-input';
                    input.name = field.name;
                    input.required = field.required;
            }

            formGroup.appendChild(label);
            formGroup.appendChild(input);
            form.appendChild(formGroup);
        });
    }

    // ä»æ¨¡æ¿åˆ›å»ºé¡µé¢
    async createFromTemplate() {
        if (!this.currentTemplate) {
            showNotification('è¯·å…ˆé€‰æ‹©æ¨¡æ¿', 'warning');
            return null;
        }

        const form = document.getElementById('template-form');
        const inputs = form.querySelectorAll('input, select, textarea');

        // æ”¶é›†è¡¨å•æ•°æ®
        const data = {};
        let isValid = true;

        inputs.forEach(input => {
            if (input.required && !input.value) {
                isValid = false;
                input.style.borderColor = '#c94f4f';
            } else {
                input.style.borderColor = '';
                data[input.name] = input.value;
            }
        });

        if (!isValid) {
            showNotification('è¯·å¡«å†™æ‰€æœ‰å¿…å¡«å­—æ®µ', 'error');
            return null;
        }

        try {
            // ç”Ÿæˆé¡µé¢æ ‡é¢˜
            let title = this.currentTemplate.name;
            if (data.productName && data.tradingDate) {
                title = `${data.productName} - ${data.tradingDate}`;
            } else if (data.productName) {
                title = data.productName;
            }

            // å¤„ç†æ¨¡æ¿å†…å®¹ï¼Œæ›¿æ¢å ä½ç¬¦
            const content = this.processTemplate(this.currentTemplate.content, data);

            // åˆ›å»ºé¡µé¢
            // è·å–å½“å‰é€‰ä¸­çš„åˆ†åŒº
            const currentSectionId = window.app ? window.app.currentSectionId : null;
            if (!currentSectionId) {
                showNotification('è¯·å…ˆé€‰æ‹©ä¸€ä¸ªåˆ†åŒº', 'error');
                return null;
            }

            const page = await db.createPage(currentSectionId, title, 'trade-log');

            // æ›´æ–°é¡µé¢å†…å®¹å’Œå…ƒæ•°æ®
            await db.updatePage(page.id, {
                content,
                metadata: {
                    ...data,
                    templateId: this.currentTemplate.id
                },
                tags: [this.currentTemplate.productType]
            });

            showNotification('é¡µé¢å·²åˆ›å»º', 'success');
            return page;

        } catch (error) {
            console.error('Error creating page from template:', error);
            showNotification('åˆ›å»ºé¡µé¢å¤±è´¥', 'error');
            return null;
        }
    }

    // å¤„ç†æ¨¡æ¿å†…å®¹ï¼Œæ›¿æ¢å ä½ç¬¦
    processTemplate(templateContent, data) {
        if (!templateContent || !templateContent.ops) {
            return { ops: [] };
        }

        const newOps = templateContent.ops.map(op => {
            if (typeof op.insert === 'string') {
                let text = op.insert;

                // æ›¿æ¢å ä½ç¬¦ {fieldName}
                Object.keys(data).forEach(key => {
                    const placeholder = `{${key}}`;
                    text = text.replace(new RegExp(placeholder, 'g'), data[key] || '');
                });

                return { ...op, insert: text };
            }
            return op;
        });

        return { ops: newOps };
    }

    // å…³é—­æ¨¡æ¿å¯¹è¯æ¡†
    closeDialog() {
        const modal = document.getElementById('modal-template');
        modal.classList.remove('active');
        this.currentTemplate = null;
    }
}

