class SearchManager {
    constructor() {
        this.searchResults = [];
        this.currentQuery = '';
    }

    // æ‰§è¡Œæœç´¢
    async search(query) {
        if (!query || query.trim().length === 0) {
            this.clearResults();
            return;
        }

        this.currentQuery = query.trim();

        try {
            const results = await db.searchPages(this.currentQuery);
            this.searchResults = results;
            this.displayResults(results);

            // æ›´æ–°çŠ¶æ€æ 
            document.getElementById('status-left').textContent =
                `æ‰¾åˆ° ${results.length} ä¸ªç»“æœ`;

        } catch (error) {
            console.error('Search error:', error);
            showNotification('æœç´¢å¤±è´¥', 'error');
        }
    }

    // æ˜¾ç¤ºæœç´¢ç»“æœ
    displayResults(results) {
        const pageList = document.getElementById('page-list');
        const currentSectionTitle = document.getElementById('current-section-title');

        // æ›´æ–°æ ‡é¢˜
        currentSectionTitle.textContent = `æœç´¢ç»“æœ: "${this.currentQuery}"`;

        // æ¸…ç©ºåˆ—è¡¨
        pageList.innerHTML = '';

        if (results.length === 0) {
            pageList.innerHTML = '<p style="color: #9ca3af; padding: 20px; text-align: center;">æœªæ‰¾åˆ°åŒ¹é…çš„é¡µé¢</p>';
            return;
        }

        // æ˜¾ç¤ºç»“æœ
        results.forEach(page => {
            const item = this.createSearchResultItem(page);
            pageList.appendChild(item);
        });
    }

    // åˆ›å»ºæœç´¢ç»“æœé¡¹
    createSearchResultItem(page) {
        const item = document.createElement('div');
        item.className = 'page-item';
        item.dataset.pageId = page.id;

        // æ ‡é¢˜ï¼ˆé«˜äº®åŒ¹é…ï¼‰
        const title = document.createElement('div');
        title.className = 'page-item-title';
        title.innerHTML = highlightText(page.title, this.currentQuery);

        // å…ƒæ•°æ®
        const meta = document.createElement('div');
        meta.className = 'page-item-meta';
        meta.innerHTML = `
            <span>${formatRelativeTime(page.updatedAt)}</span>
            <span>${this.getPageTypeLabel(page.type)}</span>
        `;

        // å†…å®¹é¢„è§ˆ
        const preview = this.getContentPreview(page, this.currentQuery);
        if (preview) {
            const previewEl = document.createElement('div');
            previewEl.style.cssText = 'font-size: 12px; color: #6b7280; margin-top: 8px; line-height: 1.4;';
            previewEl.innerHTML = preview;
            item.appendChild(title);
            item.appendChild(previewEl);
            item.appendChild(meta);
        } else {
            item.appendChild(title);
            item.appendChild(meta);
        }

        // æ ‡ç­¾
        if (page.tags && page.tags.length > 0) {
            const tagsContainer = document.createElement('div');
            tagsContainer.className = 'page-item-tags';

            page.tags.forEach(tag => {
                const tagEl = document.createElement('span');
                tagEl.className = 'tag';
                tagEl.textContent = tag;
                tagsContainer.appendChild(tagEl);
            });

            item.appendChild(tagsContainer);
        }

        // ç‚¹å‡»äº‹ä»¶
        item.addEventListener('click', () => {
            window.app.loadPage(page.id);
        });

        return item;
    }

    // è·å–å†…å®¹é¢„è§ˆ
    getContentPreview(page, query) {
        if (!page.content || !page.content.ops) return '';

        // è½¬æ¢ä¸ºçº¯æ–‡æœ¬
        let text = page.content.ops
            .map(op => typeof op.insert === 'string' ? op.insert : '')
            .join('');

        // æŸ¥æ‰¾åŒ¹é…ä½ç½®
        const lowerText = text.toLowerCase();
        const lowerQuery = query.toLowerCase();
        const index = lowerText.indexOf(lowerQuery);

        if (index === -1) return '';

        // æå–ä¸Šä¸‹æ–‡ (å‰å50ä¸ªå­—ç¬¦)
        const start = Math.max(0, index - 50);
        const end = Math.min(text.length, index + query.length + 50);

        let preview = text.substring(start, end);

        // æ·»åŠ çœç•¥å·
        if (start > 0) preview = '...' + preview;
        if (end < text.length) preview = preview + '...';

        // é«˜äº®åŒ¹é…
        return highlightText(preview, query);
    }

    // è·å–é¡µé¢ç±»å‹æ ‡ç­¾
    getPageTypeLabel(type) {
        const labels = {
            'note': 'ç¬”è®°',
            'trade-log': 'äº¤æ˜“æ—¥å¿—',
            'todo': 'å¾…åŠ'
        };
        return labels[type] || 'ç¬”è®°';
    }

    // æ¸…ç©ºæœç´¢ç»“æœ
    clearResults() {
        this.searchResults = [];
        this.currentQuery = '';

        // æ¢å¤æ˜¾ç¤ºå½“å‰åˆ†åŒºçš„é¡µé¢
        if (window.app && window.app.currentSectionId) {
            window.app.loadPagesForSection(window.app.currentSectionId);
        }

        document.getElementById('status-left').textContent = 'å°±ç»ª';
    }
}

// åˆ›å»ºå…¨å±€æœç´¢ç®¡ç†å™¨å®ä¾‹
