def to_pydate(value):
    """将各种日期格式转换为 Python date 对象"""
    ts = pd.to_datetime(value, errors='coerce')
    if pd.isna(ts):
        return None
    if isinstance(ts, datetime):
        return ts.date()
    return ts.date()

